meta.script:
//opens divi toggle from a css id in text link e.g. ..../tips-tricks/#lightbox_link
(function($){
$(window).load(function(){
var et_hash = window.location.hash;
if(window.location.hash) {
$( '.et_pb_toggle' + et_hash )
.removeClass('et_pb_toggle_close')
.addClass('et_pb_toggle_open')
}
});
})(jQuery)

(function ($) {
    $(document).ready(function () {
        $(document).bind('ready ajaxComplete', function () {
            //Add a container to the post category
            $(".gallery-blog .et_pb_post").each(function () {
                $(this).find('[rel~="category"]').wrapAll('
');
            });
            //Change the 'read more' text
            $(".gallery-blog a.more-link").text("See The Gallery");
        });
    });
})(jQuery);

jQuery(function($){
 $('.et_pb_toggle_title').click(function(){
  var $toggle = $(this).closest('.et_pb_toggle');
  if (!$toggle.hasClass('et_pb_accordion_toggling')) {
   var $accordion = $toggle.closest('.et_pb_accordion');
   if ($toggle.hasClass('et_pb_toggle_open')) {
    $accordion.addClass('et_pb_accordion_toggling');
    $toggle.find('.et_pb_toggle_content').slideToggle(700, function() { 
     $toggle.removeClass('et_pb_toggle_open').addClass('et_pb_toggle_close');

    });
   }
   setTimeout(function(){ 
    $accordion.removeClass('et_pb_accordion_toggling'); 
   }, 750);
  }
 });
});

jQuery(document).ready(function($ ) {
// add '&rel=0' to end of all YouTube video URL's
// to prevent displaying related videos
$('.pa-hide-related-videos iframe').attr( "src", function( i, val ) {
return val + '&rel=0';
});
});

