         //MOVE EVENT SLIDER ARROWS TO THE EVENTS HEADER
         $('.hp .upcomingevents .cs-article-slider-control').appendTo('.hp .upcomingevents .ui-widget-header');


         //ADD EDGE PREFIX TO IE EDGE
