            $(document).on("keydown", ".schools .sw-channel-dropdown a", function(e) {
            	var schoolsColumn = $(this).parents(".sw-channel-dropdown");
                switch(e.keyCode) {
                    // CONSUME LEFT AND UP ARROWS
                    case 37:
                    case 38:
                        e.preventDefault();
                        if($("a", schoolsColumn).index(this) == 0) {
                        	$(this).parents(".sw-channel-item").find("> a").focus();
                        } else {
                        	var index = $("a", schoolsColumn).index(this)-1;
                        	$("a", schoolsColumn).eq(index).focus();
                        }
                    break;             
                    // CONSUME RIGHT AND DOWN ARROWS
                    case 39:
                    case 40:
                        e.preventDefault();
                        if($("a", schoolsColumn).index(this) == $("a", schoolsColumn).length-1) {
                        	$(this).parents(".sw-channel-item").find("> a").focus();
                        } else {
                            var index = $("a", schoolsColumn).index(this)+1;
                            $("a", schoolsColumn).eq(index).focus();
                        }
                    break;
                    case 9:
                   		e.preventDefault();
                        var index = $("a", schoolsColumn).index(this)+1;
                        $("a", schoolsColumn).eq(index).focus();
                    break;
                  }
            });


        },

        "PositionChannelDropdowns": function() {
        	if(this.GetBreakPoint() == "desktop") {
                $(".sw-channel-item").on('mouseenter mouseleave', function (e) {
                  if ($('.sw-channel-dropdown', this).length) {
                      var elm = $('.sw-channel-dropdown', this);
                      var off = elm.offset();
                      var l = off.left;
                      var w = elm.width();
                      var docW = $("header").width();

                      var isEntirelyVisible = (l + w <= docW);
                      if (!isEntirelyVisible) {
                          $(this).addClass('edge');
                      } else {
                          $(this).removeClass('edge');
                      }
                  }
              });

            }


        },

