            $(document).on("keydown", ".mega-columns a", function(e) {
                var megaColumn = $(this).parents(".mega-columns");
                switch(e.keyCode) {
                    // CONSUME LEFT AND UP ARROWS
                    case 37:
                    case 38:
                        e.preventDefault();
                        if($("a", megaColumn).index(this) == 0) {
                            $(this).parents(".sw-channel-item").find("> a").focus();
                        } else {
                            var index = $("a", megaColumn).index(this)-1;
                            $("a", megaColumn).eq(index).focus();
                        }
                    break;             
                    // CONSUME RIGHT AND DOWN ARROWS
                    case 39:
                    case 40:
                        e.preventDefault();
                        if($("a", megaColumn).index(this) == $("a", megaColumn).length-1) {
                            $(this).parents(".sw-channel-item").find("> a").focus();
                        } else {
                            var index = $("a", megaColumn).index(this)+1;
                            $("a", megaColumn).eq(index).focus();
                        }
                    break;
                    case 9:
                        e.preventDefault();
                        var index = $("a", megaColumn).index(this)+1;
                        $("a", megaColumn).eq(index).focus();
                    break;
                  }
            });

            $("li.sw-channel-item.mega-channel > a").on("click", function(e) {
            	e.preventDefault();
            });

        },

        "Body": function() {
        	var _this = this;
            // AUTO FOCUS SIGN IN FIELD
            $("#swsignin-txt-username").focus();

            // APPLY RESPONSIVE DIMENSIONS TO CONTENT IMAGES
            $("div.ui-widget.app .ui-widget-detail img")
                .not($("div.ui-widget.app.multimedia-gallery .ui-widget-detail img"))
                .not($("div.ui-widget.app.gallery.json .ui-widget-detail img"))
                .each(function() {
                    if ($(this).attr('width') !== undefined && $(this).attr('height') !== undefined) { // IMAGE HAS INLINE DIMENSIONS
                        $(this).css({"display": "inline-block", "width": "100%", "max-width": $(this).attr("width") + "px", "height": "auto", "max-height": $(this).attr("height") + "px"});
                    }
            });

            // ADJUST FIRST BREADCRUMB
            $("li.ui-breadcrumb-first > a > span").text("Home");


            // USE CHANNEL NAME FOR PAGELIST NAV HEADER IF ONE IS NOT PRESENT
            if(!$("div.sp.column.one .ui-widget-header h1").length) {
                $("div.sp.column.one .ui-widget-header").append("
");
            }


            //ADD CLASS TO CHANNELS WITH DROPDOWN
            $('.sw-channel-dropdown').closest('li').addClass('has-dropdown');

            //ADD CLASS TO EVENT LIS ON HOVER
            $('.hp .upcomingevents li').hover(function() {
            	$(this).addClass('hovered');
            }, function() {
            	$(this).removeClass('hovered');
            });



            //SCROLL DOWN BUTTON FUNCTIONALITY 
            $("#down").on('click keydown', function(e){
            	if(_this.AllyClick(e)) {
                  e.preventDefault();
                  $("html, body").animate({ scrollTop:$('#sec-one-app').position().top}, 'slow');

                  $('#sec-one-app li:first').find('a:first').focus();
                }
             });

        },

