        "Search": function() {
        var _this = this;

             // //DON"T ALLOW USER TO SUBMIT AN EMPTY FORM
            $('#sw-search-input').off();
            $('#gb-search-input').on('keydown', function(e){
                if(e.which == 13){//Enter key pressed
                    e.preventDefault();
                     if($.trim($("#gb-search-input").val()) != "Search..." && $.trim($("#gb-search-input").val()) != "") {
                        window.location.href = "/site/Default.aspx?PageType=6&SiteID=4&SearchString=" + $("#gb-search-input").val();
                    }

                } 


            });

            $("#sw-search-button").off().removeAttr("onclick").attr("href", "#");
            $("#gb-search-submit").on("click keydown", function(e) {
                if(_this.AllyClick(e)) {
                    e.preventDefault();

                    if($.trim($("#gb-search-input").val()) != "Search..." && $.trim($("#gb-search-input").val()) != "") {
                        window.location.href = "/site/Default.aspx?PageType=6&SiteID=4&SearchString=" + $("#gb-search-input").val();
                    }
                }


            });

            $('#gb-search-btn').on('click', function() {
            	if($(this).attr("aria-expanded") == "false") {
                	$(this).attr('aria-expanded', true).attr('aria-label', 'Close Search Form');
                    $('#gb-search-input, #gb-search-submit').slideDown('fast');
                    $('#gb-search-input').focus();

                }else {
                	$(this).attr('aria-expanded', false).attr('aria-label', 'Open Search Form');
                    $('#gb-search-input, #gb-search-submit').slideUp('fast');

                }

            });

            //SEARCH TEXT
            $("#gb-search-input").focus(function() {
                if($(this).val() == "Search...") {
                    $(this).val("");
                }
            }).blur(function() {
                if($(this).val() == "") {
                    $(this).val("Search...");
                }
            });;


            //UPDATE SEARCH STATE ON FOCUS AND MOUSE EVENTS
            $('#gb-search-form').on('mouseenter focusin', function() {
            	$(this).addClass('interacting');
            });


            $('#gb-search-form').on('focusout mouseleave', function() {
                 setTimeout(function () {
                    if(!$('#gb-search-form').find(":focus").length) {
                        $('#gb-search-btn').attr('aria-expanded', false).attr('aria-label', 'Open Search Form');
                        $('#gb-search-input, #gb-search-submit').slideUp('fast');
                        $('#gb-search-form').removeClass('interacting');
                    }
                }, 100);

            });


        },

        "AllyClick": function(event) {
            if(event.type == "click") {
                return true;
            } else if(event.type == "keydown" && (event.keyCode == this.KeyCodes.space || event.keyCode == this.KeyCodes.enter)) {
                return true;
            } else {
                return false;
            }
        },





        "ModEvents":function() {
        	if (!$(".upcomingevents .joel-month").length) {
                $(".upcomingevents").modEvents({
                    columns     : "yes",
                    monthLong   : "yes",
                    dayWeek     : "yes"
                });
                this.EventsByDay(".upcomingevents .ui-articles");
            }







        },

        "EventsByDay":function(container) {

		var countLi = 0;
		$(".ui-article", container).each(function(){
			if (!$(this).find("h1.ui-article-title.sw-calendar-block-date").size()){
				var moveArticle = $(this).html();
				$(this).parent().prev().children().children().next().append(moveArticle);
				$(this).parent().remove();
			};
		});
		$(".ui-article", container).each(function(){
			var newDateTime = $('.upcoming-column.left h1', this).html().split("");
			var newDate = '';
			var dateIndex = ((newDateTime.length > 2) ? 2 : 1); //if the dayWeek is set to yes we need to account for that in our array indexing

			//add a zero to the date if it is a single digit
			if( newDateTime[dateIndex].length == 1 ) newDateTime[dateIndex] = "0" + newDateTime[dateIndex];

			//if we have the day of the week make sure to include it.
			if( dateIndex > 1 ){ 
				newDate += newDateTime[0] + "";
			}

			//add in the month
			newDate += newDateTime[dateIndex - 1] + ''; //the month is always the in the left array position to the date

			//wrap the date in a new tag
			newDate += ''+ " "+'' + newDateTime[dateIndex] + '';

			//append the date and month back into their columns
			$('.upcoming-column.left h1', this).html(newDate);	


			//add an ALL DAY label if no time was given
			$('.upcoming-column.right .ui-article-description', this).each(function(){
				if( $('.sw-calendar-block-time', this).length < 1 ){ //if it doesnt exist add it
					$(this).prepend('ALL DAY');
				}


			});


			//add an ALL DAY label if no time was given
			$('.upcoming-column.right .ui-article-description', this).each(function(){
				if( $('.sw-calendar-block-time', this).length < 1 ){ //if it doesnt exist add it
					$(this).prepend('ALL DAY');
				}


			});
             //WRAP DATE AND MONTH IN A CONTAINER
                    //$(".joel-day, .joel-month", this).wrapAll("");


		});




        $('.upcomingevents').find(".ui-articles li").each(function(){

                  var eventMonth = $(this).find(".joel-month").text();
                  var eventNumberDay = $(this).find(".jeremy-date").text();
                  var eventDay = $(this).find(".joel-day").remove();

                  var ariaMonth = "";
                  var ariaDate = "";
                  switch(eventMonth){
                    case "jan":
                      ariaMonth = "January";
                    break;
                    case "feb":
                      ariaMonth = "February";
                    break;
                    case "mar":
                      ariaMonth = "March";
                    break;
                    case "apr":
                      ariaMonth = "April";
                    break;
                    case "may":
                      ariaMonth = "May";
                    break;
                    case "jun":
                      ariaMonth = "June";
                    break;
                    case "jul":
                      ariaMonth = "July";
                    break;
                    case "aug":
                      ariaMonth = "August";
                    break;
                    case "sep":
                      ariaMonth = "September";
                    break;
                    case "oct":
                      ariaMonth = "October";
                    break;
                    case "nov":
                      ariaMonth = "November";
                    break;
                    case "dec":
                      ariaMonth = "December";
                    break;
                    default: ariaMonth = eventMonth;
                  }
                  switch(eventNumberDay){
                    case "1":
                      ariaDate = "First";
                    break;
                    case "2":
                      ariaDate = "Second";
                    break;
                    case "3":
                      ariaDate = "Third";
                    break;
                    case "4":
                      ariaDate = "Fourth";
                    break;
                    case "5":
                      ariaDate = "Fifth";
                    break;
                    case "6":
                      ariaDate = "Sixth";
                    break;
                    case "7":
                      ariaDate = "Seventh";
                    break;
                    case "8":
                      ariaDate = "Eighth";
                    break;
                    case "9":
                      ariaDate = "Ninth";
                    break;
                    case "10":
                      ariaDate = "Tenth";
                    break;
                    case "11":
                      ariaDate = "Eleventh";
                    break;
                    case "12":
                      ariaDate = "Twelveth";
                    break;
                    case "13":
                      ariaDate = "Thirteenth";
                    break;
                    case "14":
                      ariaDate = "Fourteenth";
                    break;
                    case "15":
                      ariaDate = "Fifteenth";
                    break;
                    case "16":
                      ariaDate = "Sixteenth";
                    break;
                    case "17":
                      ariaDate = "Seventeenth";
                    break;
                    case "18":
                      ariaDate = "Eighteenth";
                    break;
                    case "19":
                      ariaDate = "Nineteenth";
                    break;
                    case "20":
                      ariaDate = "Twentieth";
                    break;
                    case "21":
                      ariaDate = "Twenty-first";
                    break;
                    case "22":
                      ariaDate = "Twenty-second";
                    break;
                    case "23":
                      ariaDate = "Twenty-third";
                    break;
                    case "24":
                      ariaDate = "Twenty-fourth";
                    break;
                    case "25":
                      ariaDate = "Twenty-fifth";
                    break;
                    case "26":
                      ariaDate = "Twenty-sixth";
                    break;
                    case "27":
                      ariaDate = "Twenty-seventh";
                    break;
                    case "28":
                      ariaDate = "Twenty-eighth";
                    break;
                    case "29":
                      ariaDate = "Twenty-ninth";
                    break;
                    case "30":
                      ariaDate = "Thirtieth";
                    break;
                    case "31":
                      ariaDate = "Thirty-first";
                    break;
                    default: ariaDate = eventNumberDay;
                  }

                  $(this).find('.sw-calendar-block-date').attr('aria-label', ariaMonth+" "+ariaDate);

                })



        },

        "MmgCollage": function() {
        	var _this = this;

        	if($("#sw-content-container3.ui-hp .ui-widget.app.multimedia-gallery").length) {
                var mmg = eval("multimediaGallery" + $("#sw-content-container3.ui-hp .ui-widget.app.multimedia-gallery:first").attr("data-pmi"));
