' + record.caption + '' : '') +
                                            	((record.isLinked == "True" || record.videoIsEmbedded == "True") ? '
' : '') +
                                            		((record.isLinked == "True") ? '' + record.linkText + '' : '') +
                                            		((record.videoIsEmbedded == "True") ? '' + record.videoLinkText + '' : '') +
                                            	((record.isLinked == "True" || record.videoIsEmbedded == "True") ? '' : '') +
                                        	'' +
                                        '' +
                                    '';


                    if(index == 9) return false;

                });

                if(mmgRecords) {



                	$("#sw-content-container3.ui-hp .ui-widget.app.multimedia-gallery:first .ui-widget-detail").prepend('' + mmgRecords + '');
                }

                $(".ui-widget.app.multimedia-gallery.collage").on("focusin touchstart", "li", function(e) {
                	$(this).addClass("focus");
                    $(".ui-widget.app.multimedia-gallery.collage .cs-mobile-collage-description").html($(".ui-article-inner", this).html());
