body.script:
window.onload = init;
var topnavButton = document.getElementById('topnav_mobile');
var topnavDisplay = document.getElementById('topnav');

function init() {
	topnavButton.onclick = toggleNav;
}

function toggleNav(){
	topnavDisplay.classList.toggle("open");
}
