var jq = jQuery.noConflict(true);
jq(document).ready(function(){
    jq(".countdown-active").TimeCircles({
            "animation": "smooth",
            "bg_width": 1,
            "fg_width": 0.05,
            "text_size": .1,
            "count_past_zero": false,
            "circle_bg_color": "#92150F",
            "time": {
                    "Days": {
                            "text": "Days",
                            "color": "#CCCCCC",
                            "show": true
                    },
                    "Hours": {
                            "text": "Hours",
                            "color": "#CCCCCC",
                            "show": true
                    },
                    "Minutes": {
                            "text": "Minutes",
                            "color": "#CCCCCC",
                            "show": true
                    },
                    "Seconds": {
                            "text": "Seconds",
                            "color": "#CCCCCC",
                            "show": true
                    }
            }
    });
    //rebuild when window size changes
    jq(window).resize(function() {
            jq(".countdown-active").TimeCircles().rebuild();
    });
});

