        },
        'disclaimerLink': {
            'show': false,
            'linkText': 'Disclaimer',
            'linkUrl': 'https://dcps.duvalschools.org/Page/9380',
            'openLinkInNewWindow': false
        },
        'disclaimerColors': {
            'background': '#000000',
            'textColor': '#FFFFFF',
            'linkColor': '#FFFFFF'
        },
        'templateStickyFooter': {
            'hasStickyFooter': false,
            'stickyFooterID': '' // Example: '#gb-footer-fixed'
        }
    });
});

	var csKeyCodes = { "tab": 9, "enter": 13, "esc": 27, "space": 32, "end": 35, "home": 36, "left": 37, "up": 38, "right": 39, "down": 40 };

	var district = "dcps";
	var featureTimerSet = 0;

	$(window).load(function(){
		if($(window).width() >= 320 && $(window).width() <= 639){
			calendarListView();
		}
		$(".icon-font").removeClass("hidden");
	});

	$(function(){
		// SET IE CLASSES TO BODY
		if(isMSIE){ $("body").addClass("msie"); }
