			if(!$(this).hasClass("hidden")) {
				globalIcons += '1, ' + $(".text", this).text() + ', ' + $(this).attr("href") + ', _self;';
			}
		});
		var globalIconsHeader = (globalIcons != "") ? "Popular Links" : "";

		// RESPONSIVE MENU
        $("body").csRsMenu({
