            "extraMenuItems" : globalIcons, // EXTRA MENU BUTTONS - STRUCTURE IS: MENU-OPTION-NUMBER, ITEM-NAME, ITEM-URL, ITEM-TARGET;
            "siteID" : "4",
            "allLoaded" : function(){ }
        });

        $("#hp-content").csAppAccordion({
