	});	

	function pgScreenSize(){
		var getWidth = $(window).width();
		if (getWidth >= 1024) { // 960+
			photoGallery();
			featureRotateTimer();
            $(".hp-feature-slide").attr({"aria-hidden": "true", "role": "tabpanel"});
            $(".hp-feature-slide.active").attr("aria-hidden", "false");
            $(".hp-feature-slide-body").removeAttr("aria-hidden");
		} if (getWidth >= 768 && getWidth < 1024) { // 768+
			photoGallery();
			if(featureTimerSet == 1){
				clearInterval(window.featureTimer);
				featureTimerSet = 0;
			}
            if(!$("#hp-content .hp.column div.ui-widget.app.open").length){
                $("#hp-content .hp.column div.ui-widget.app:first").addClass("open");
                $("#hp-content .hp.column div.ui-widget.app:first .ui-widget-detail").show().attr("aria-hidden", "false");
                $("#hp-content .hp.column div.ui-widget.app:first .ui-widget-footer").show().attr("aria-hidden", "false");
            }
            $(".hp-feature-slide").attr({"aria-hidden": "true", "role": "tabpanel"});
            $(".hp-feature-slide.active").attr("aria-hidden", "false");
            $(".hp-feature-slide-body").removeAttr("aria-hidden");
		} if (getWidth >= 640 && getWidth < 768) { // 640+
			photoGallery();
			if(featureTimerSet == 1){
				clearInterval(window.featureTimer);
				featureTimerSet = 0;
			}
            if(!$("#hp-content .hp.column div.ui-widget.app.open").length){
                $("#hp-content .hp.column div.ui-widget.app:first").addClass("open");
                $("#hp-content .hp.column div.ui-widget.app:first .ui-widget-detail").show().attr("aria-hidden", "false");
                $("#hp-content .hp.column div.ui-widget.app:first .ui-widget-footer").show().attr("aria-hidden", "false");
            }
            $(".hp-feature-slide").attr({"aria-hidden": "true", "role": "tabpanel"});
            $(".hp-feature-slide.active").attr("aria-hidden", "false");
            $(".hp-feature-slide-body").removeAttr("aria-hidden");
		} if (getWidth >= 480 && getWidth < 640) { // 480+
			photoGallery();
			hpFeatureAppView();
			if(featureTimerSet == 1){
				clearInterval(window.featureTimer);
				featureTimerSet = 0;
			}
            if(!$("#hp-content .hp.column div.ui-widget.app.open").length){
                $("#hp-content .hp.column div.ui-widget.app:first").addClass("open");
                $("#hp-content .hp.column div.ui-widget.app:first .ui-widget-detail").show().attr("aria-hidden", "false");
                $("#hp-content .hp.column div.ui-widget.app:first .ui-widget-footer").show().attr("aria-hidden", "false");
            }
            $(".hp-feature-slide .hp-feature-slide-header").attr("aria-expanded", "false");
            $(".hp-feature-slide.active .hp-feature-slide-header").attr("aria-expanded", "true");
            $(".hp-feature-slide").attr("aria-hidden", "false").removeAttr("role");
            $(".hp-feature-slide-body").attr("aria-hidden", "true");
            $(".hp-feature-slide.active .hp-feature-slide-body").attr("aria-hidden", "false");
		} if (getWidth >= 320 && getWidth < 480) { // 320+
			hpFeatureAppView();
			if(featureTimerSet == 1){
				clearInterval(window.featureTimer);
				featureTimerSet = 0;
			}
            if(!$("#hp-content .hp.column div.ui-widget.app.open").length){
                $("#hp-content .hp.column div.ui-widget.app:first").addClass("open");
                $("#hp-content .hp.column div.ui-widget.app:first .ui-widget-detail").show().attr("aria-hidden", "false");
                $("#hp-content .hp.column div.ui-widget.app:first .ui-widget-footer").show().attr("aria-hidden", "false");
            }
            $(".hp-feature-slide .hp-feature-slide-header").attr("aria-expanded", "false");
            $(".hp-feature-slide.active .hp-feature-slide-header").attr("aria-expanded", "true");  
            $(".hp-feature-slide").attr("aria-hidden", "false").removeAttr("role");
            $(".hp-feature-slide-body").attr("aria-hidden", "true");
            $(".hp-feature-slide.active .hp-feature-slide-body").attr("aria-hidden", "false");
		}
	}

