	function hpContentAppView(){
		//DOC make homepage apps collapsible for small mobile devices
		if(!$("#hp-content .hp.column div.ui-widget.app.active").length){
			$("#hp-content .hp.column div.ui-widget.app:first").addClass("active");
		}
		$("#hp-content .hp.column div.ui-widget-header").click(function(){
			var clickedApp = $(this).parent().parent().attr("id");

			$("#hp-content .hp.column div.ui-widget.app").removeClass("active");
			$(this).parent().addClass("active");

			$("html, body").animate({
				scrollTop: $("#" + clickedApp).offset().top
			}, 500, function(){
				$("html, body").stop(true, false);
			});
		});
	}

	function hpFeatureAppView(){
		//DOC make homepage apps collapsible for small mobile devices
		if(!$("#hp-feature-content div.hp-feature-slide.active").length){
			$("#hp-feature-content div.hp-feature-slide:first").addClass("active");
		}
		$("#hp-feature-content div.hp-feature-slide-header").on("click keydown", function(e){
        	if(allyClick(e)) {
				e.preventDefault();
                var clickedApp = $(this).parent().parent().attr("id");

                $("#hp-feature-content div.hp-feature-slide").removeClass("active");
                $(".hp-feature-slide-header").attr("aria-expanded", "false");
                $(this).attr("aria-expanded", "true");
                $(this).parent().addClass("active");

                $(".hp-feature-slide-body").attr("aria-hidden", "true");
                $("+ .hp-feature-slide-body", this).attr("aria-hidden", "false");

                $("html, body").animate({
                    scrollTop: $("#" + clickedApp).offset().top
                }, 500, function(){
                    $("html, body").stop(true, false);
                });
            }
		});
	}

