		siteName = siteName.split(" ");
		var siteNameLength = siteName.length;
		siteNameEnd = jQuery.trim(siteName.splice(-siteNameBreak, siteName.length).toString().replace(/,/g, " "));
		siteNameBegin = jQuery.trim(siteName.toString().replace(/,/g, " "));
		$("#gb-sitename").prepend("
" + siteNameBegin + " " + siteNameEnd + "");
		var sitenameSize = 'Medium';
		if(sitenameSize != "Small;Medium;Large"){
			switch(sitenameSize){
				case "Small":
					$("#gb-sitename h1").css("font-size", "20px");
					$("#gb-tagline").css("font-size", "12px");
				break;
				case "Medium":
					$("#gb-sitename h1").css("font-size", "30px");
					$("#gb-tagline").css("font-size", "13px");
				break;
				case "Large":
					// DO NOTHING
				break;
				default:
