                        $("#gb-school-list-outer").slideDown(function(){
                            $("#school-select-btn").removeClass("closed").addClass("open");
                            $("#gb-school-list-outer").removeClass("closed").addClass("open").attr("aria-hidden", "false");
                            if(e.type == "keydown") {
