        	});

	        	if($("#hp-feature-content .ui-widget.app").length) {
	        		$("#hp-feature-block-outer").removeClass("hidden");
	        	}

	        	if("dcps" == district){
	        		// SHOW THE FEATURE BLOCK
	        		//$("#hp-feature-block-outer").removeClass("hidden");

