		        		$(".hp-feature-slide." + featureNum).remove();

		        		// REMOVE THE VERTICAL DIVIDER
	        			$(this).parent().parent().prev(".vert").remove();

		        		// REMOVE THE TITLE
	        			$(this).parent().parent().remove();
	        		}
