	function schoolListEvents(){
		// TAB HEADER CLICK
		$("#gb-school-list").on("click keydown", ".gb-school-list-header", function(e) {
        	if(e.type == "click") {
            	switchTabs($(".gb-school-list-header.active"), $(this), ".gb-school-list-section");
            } else if(e.type = "keydown") {
            	handleTabEvent($(this), e, ".gb-school-list-header", ".gb-school-list-section");
            }
		});

        $("#gb-school-list").focusout(function() {
        	setTimeout(function() {
                if(!$("#gb-school-list").find(":focus").length && !$("#school-select-btn").is(":focus")) {
                    $("#school-select-btn").attr("aria-expanded", "false");
                    $("#gb-school-list-outer").slideUp(function(){
                        $("#school-select-btn").removeClass("open").addClass("closed");
                        $("#gb-school-list-outer").removeClass("open").addClass("closed").attr("aria-hidden", "true");
                    });
                }
            }, 500);
        });

        // LINK KEYDOWN EVENTS
        $(".gb-school-list-section a").attr("tabindex", "-1");
        $("#gb-school-list").on("keydown", ".gb-school-list-section a", function(e) {
            // CAPTURE KEY CODE
            switch(e.keyCode) {
                // CONSUME LEFT AND UP ARROWS
                case csKeyCodes.left:
                case csKeyCodes.up:
                    e.preventDefault();

                    // IS FIRST ITEM
                    if($(this).parent().is(":first-child")) {
                    	if($(this).closest(".gb-school-list-column").is(":first-child")) {
                        	// FOCUS ACTIVE TAB
                        	$(".gb-school-list-section a").attr("tabindex", "-1");
                        	$(".gb-school-list-header.active").focus();
                        } else {
                        	$(".gb-school-list-section a").attr("tabindex", "-1");
                        	$(this).closest(".gb-school-list-column").prev(".gb-school-list-column").find("ul li:last-child > a").attr("tabindex", "0").focus();
                        }
                    } else {
                        // FOCUS PREVIOUS ITEM
                        $(".gb-school-list-section a").attr("tabindex", "-1");
                        $(this).parent().prev("li").find("> a").attr("tabindex", "0").focus();
                    }
                break;

                // CONSUME RIGHT AND DOWN ARROWS
                case csKeyCodes.right:
                case csKeyCodes.down:
                    e.preventDefault();

                    // IS LAST ITEM
                    if($(this).parent().is(":last-child")) {
                    	if($(this).closest(".gb-school-list-column").is(":last-child")) {
                        	// FOCUS ACTIVE TAB
                        	$(".gb-school-list-section a").attr("tabindex", "-1");
                        	$(".gb-school-list-header.active").focus();
                        } else {
                        	// FOCUS FIRST ITEM
                        	$(".gb-school-list-section a").attr("tabindex", "-1");
                        	$(this).closest(".gb-school-list-column").next(".gb-school-list-column").find("ul li:first-child > a").attr("tabindex", "0").focus();
                        }
                    } else {
                        // FOCUS NEXT ITEM
                        $(".gb-school-list-section a").attr("tabindex", "-1");
                        $(this).parent().next("li").find("> a").attr("tabindex", "0").focus();
                    }
                break;

                // CONSUME TAB KEY
                case csKeyCodes.tab:
                    if(e.shiftKey) {
                        e.preventDefault();

                        // FOCUS DROPDOWN BUTTON
                        $(".gb-school-list-section a").attr("tabindex", "-1");
                        $(".gb-school-list-header.active").focus();
                    }
                break;
            }
        });

		// ELEMENTARY ACCORDION CLICK
		$(".gb-elem-cat-header").click(function(e){
			e.preventDefault();
			// IF THE SELECTED ACCORDION MENU OPTION ISNT ALREADY ACTIVE
			if(!$(this).parent().hasClass("active")){
				// GET CLASS OF SELECTED ACCORDION MENU OPTION
				var selectedCat = $(this).parent().attr("class");
				selectedCat = jQuery.trim(selectedCat.replace("gb-elem-cat ", ""));

				$(".gb-elem-cat.active .gb-elem-cat-list").slideUp(function(){
					// HIDE THE ACTIVE ACCORDION MENU OPTION
					$(".gb-elem-cat.active").removeClass("active");
				});

				$(".gb-elem-cat." + selectedCat + " .gb-elem-cat-list").slideDown(function(){
					$(".gb-elem-cat." + selectedCat).addClass("active");
				})
			}
		});
	}

    function switchTabs(currTab, nextTab, panel) {
        // SWITCH TAB
        $(currTab).removeClass("active").attr("aria-selected", "false").attr("tabindex", "-1");
        $(nextTab).addClass("active").attr("aria-selected", "true").attr("tabindex", "0").focus();

        // SWITCH PANEL
        var nextIndex = parseInt(nextTab.attr("data-index"));
        $(panel).attr("aria-hidden", "true").removeClass("active");
        $(panel + "[data-index='" + nextIndex + "']").attr("aria-hidden", "false").addClass("active");
    }

    function handleTabEvent(currTab, e, tab, panel) {
        var lastIndex = $(tab).length - 1;

        // ONLY CONSUME IF NOT USING ALT KEY
        if (!e.altKey) {
            switch(e.keyCode) {
                // MOVE ONE STEP BACKWARDS
                case 37 : // LEFT ARROW
                case 38 : // UP ARROW
                    if (!e.ctrlKey) { // CTRL+ARROW MOVES FOCUS FROM PANEL TO OPEN TAB - MAKE SURE THAT IS NOT BEING PRESSED TOO
                        e.preventDefault();
                        e.stopPropagation();

                        // GET THE PREVIOUS TAB
                        var prevTab;
                        if (parseInt(currTab.attr("data-index")) == 0) { 
                            prevTab = $(tab + "[data-index='" + lastIndex + "']"); // LAST TAB
                        } else { 
                            prevTab = $(tab + "[data-index='" + (parseInt(currTab.attr("data-index")) - 1) + "']"); // PREVIOUS TAB
                        } 

                        // SWITCH THE TABS
                        switchTabs(currTab, prevTab, panel);
                    }
                break;

                // MOVE ONE STEP FORWARDS
                case 39 : // RIGHT ARROW
                case 40 : // DOWN ARROW
                    if (!e.ctrlKey) { // CTRL+ARROW MOVES FOCUS FROM PANEL TO OPEN TAB - MAKE SURE THAT IS NOT BEING PRESSED TOO
                        e.preventDefault();
                        e.stopPropagation();

                        // GET THE NEXT TAB
                        var nextTab;
                        if (parseInt(currTab.attr("data-index")) == lastIndex) { 
                            nextTab = $(tab + "[data-index='0']"); // FIRST TAB
                        } else { 
                            nextTab = $(tab + "[data-index='" + (parseInt(currTab.attr("data-index")) + 1) + "']"); // PREVIOUS TAB
                        } 

                        // SWITCH THE TABS
                        switchTabs(currTab, nextTab, panel);
                    }
                break;

                case 9: // TAB KEY
                    e.preventDefault();
                    e.stopPropagation();

                    $(panel + ".active a:first").attr("tabindex", "0").focus();
                break;

                // JUMP TO FIRST TAB
                case 36 : // HOME KEY
                    e.preventDefault();
                    e.stopPropagation();

                    switchTabs(currTab, $(tab + "[data-index='0']"), panel);
                break;

                // JUMP TO LAST TAB
                case 35 : // END KEY
                    e.preventDefault();
                    e.stopPropagation();

                    switchTabs(currTab, $(tab + "[data-index='" + lastIndex + "']"), panel);
                break;
            }
        }
    }

	function removeLogo(){
		$("#gb-logo").remove();
		$("#gb-sitename").css("width", "auto");
	}

	function modEvents(){
		if (!$(".upcomingevents .joel-month").length) {
			$(".upcomingevents").modEvents({
				columns		: "yes",
				monthLong	: "no"
			});
			eventsByDay(".ui-hp .upcomingevents .ui-articles");
		}
	}

	function eventsByDay(container) {
		var countLi = 0;
		$(".ui-article", container).each(function(){
			if (!$(this).find("h1.ui-article-title.sw-calendar-block-date").size()){
				var moveArticle = $(this).html();
				$(this).parent().prev().children().children().next().append(moveArticle);
				$(this).parent().remove();
			};
		});
	}

	function rotateFeatures(){
		$(".hp-feature-title.active").removeClass("active").next("img").next(".hp-feature-title").addClass("active");

		if(!$(".hp-feature-title.active").length){
			$(".hp-feature-title").first().addClass("active");
			$(".hp-feature-slide").removeClass("active");
			$(".hp-feature-slide").first().addClass("active");
		} else {
			$(".hp-feature-slide.active").removeClass("active").next(".hp-feature-slide").addClass("active");
		}
	}

	function featureRotateTimer(){
		var getWidth = $(window).width();
		if($(".hp-feature-title").length > 1 && getWidth >= 1024 && featureTimerSet == 0){
			window.featureTimer = setInterval(function(){
				rotateFeatures();
			}, 8000);
			featureTimerSet = 1;
		}
	}

