    function allyClick(event) {
        if(event.type == "click") {
            return true;
        } else if(event.type == "keydown" && (event.keyCode == csKeyCodes.space || event.keyCode == csKeyCodes.enter)) {
            return true;
        } else {
            return false;
        }
    }

