link.script:
	$(window).load(function(){
		searchText();
		calendarListView();

			//Setup the bullets on navigation apps
		$('.ui-widget.app.navigation').each(function(k, v){ //** go through every app
			$(this).children('.ui-widget-detail')
			.children('ul')
			.children('li')
 			.each(function(k1, v1){ //** go through all of the apps links
				if($(this).find('.bullet').length > 1){
					$(this).find('.bullet').each(function(k2, v2){ //this has bullet children
						setBullet($(this));
					});
				}else{ //this is the only bullet level
					setBullet($(this).find('.bullet'));
				}
			});
		});

	});

	/*setBullet
		Use: to set bullets with chevrons appropriately 
		Parameter: bullet (jQuery Obj) - This is a jQuery Obj of the bullet
	*/
	function setBullet(bullet){
		if($(bullet).hasClass('expandable')){ //if it has children apply a different bullet
			$(bullet).addClass('icon-font icon-chevron-down');
		}else{	//no children give it a normal bullet
			$(bullet).addClass('icon-font icon-chevron-right');
		}
	}

	$(function(){
		$(window).bind("resize", function(){ pgScreenSize(); calendarListView(); });

		// RESPONSIVE MAIN MENU
		$("body").csRsMenu({
		        "breakPoint"            : 768, // SYSTEM BREAK POINTS - 768, 640, 480, 320
		        "menuButtonParent"      : "#gb-channel-list > div",
		        "showMenuBtnText"       : "yes",
		        "menuBtnText"           : "MAIN MENU ",
		        "showSchools"           : "yes",
		        "showTranslate"         : "yes",
		        "translateId"           : "none",
		        "showAccount"           : "yes",
		        "extraMenuOptions"      : "", // EXTRA MENU HEADING NAMES SEPARATED BY A ;
		        "extraMenuItems"        : "", // EXTRA MENU BUTTONS - STRUCTURE IS: MENU-OPTION-NUMBER, ITEM-NAME, ITEM-URL, ITEM-TARGET;
		        "siteID"                : "4"
		});

		// RESPONSIVE PAGELIST MENU
		$("body").csRsPagelistMenu({
		        "breakPoint"            : 480, // SYSTEM BREAK POINTS - 768, 640, 480, 320
		        "menuButtonParent"      : "#sp-content",
		        "showMenuBtnText"       : "yes",
		        "menuBtnText"           : "PAGES",
		        "siteID"                : "4"
		});

        creativeTranslate();
		pgScreenSize();
		useChannelForSection();
		dynamicContent();
		modEvents();
		customColors();


		//Custom Dropdown Functionality
		$('.custom-dropdown').click(function(e){
		    if( $(this).hasClass("openMenu") ){
		        $(this).removeClass("openMenu");
		        $(this).children('p').children('span').removeClass('icon-chevron-up').addClass('icon-chevron-down');
		    }else{
		        $('.custom-dropdown.openMenu').removeClass('openMenu').children('p').children('span').removeClass('icon-chevron-up').addClass('icon-chevron-down');
		        $(this).addClass("openMenu");
		        $(this).children('p').children('span').removeClass('icon-chevron-down').addClass('icon-chevron-up');
		    }
		})
		.hover(function(e){},function(e){
		    $(this).removeClass("openMenu");
		    $(this).children('p').children('span').removeClass('icon-chevron-up').addClass('icon-chevron-down');
		});	

        //Custom Dropdown Functionality
		$('.sw-mystart-dropdown.translate').click(function(e){
		    if($(".sw-dropdown-arrow", this).hasClass("icon-chevron-up") ){
		        //$(".sw-dropdown-arrow", this).removeClass('icon-chevron-up').addClass('icon-chevron-down');
		    }else{
		        $(".sw-dropdown-arrow", this).removeClass('icon-chevron-down').addClass('icon-chevron-up');
		    }
		})
		.hover(function(e){},function(e){
		    $(".sw-dropdown-arrow", this).removeClass('icon-chevron-up').addClass('icon-chevron-down');
		});

	});	

	function pgScreenSize(){
		var getWidth = $(window).width();
		if (getWidth >= 1024) { // 960+
			photoGallery();
			$('#gb-links').appendTo('#gb-header');
			$('#gb-footer').append($('#gb-footer-legal-outer'));

		} if (getWidth >= 768 && getWidth < 1024) { // 768+
			photoGallery();
			$('#gb-links').appendTo('#gb-header');
			$('#gb-footer-outer').after($('#gb-footer-legal-outer'));

		} if (getWidth >= 640 && getWidth < 768) { // 640+
			photoGallery();
			$('#gb-search-open').before($('#gb-links'));
			$('#gb-footer-outer').after($('#gb-footer-legal-outer'));
			customColors();

		} if (getWidth >= 480 && getWidth < 640) { // 480+
			photoGallery();
			appView();
			$('#gb-search-open').before($('#gb-links'));
			$('#gb-footer-outer').after($('#gb-footer-legal-outer'));
			customColors();

		} if (getWidth >= 320 && getWidth < 480) { // 320+
			appView();
			$('#gb-mobile-links-container').append($('#gb-links'));
			$('#gb-footer-outer').after($('#gb-footer-legal-outer'));
		}
	}

	function photoGallery(){
		if(!$("#sw-content-container10.region.ui-hp .rs-photo-gallery-container").length){
			// PHOTO GALLERY ROTATOR
			$("#sw-content-container10.ui-hp div.ui-widget.app.gallery.json").creativeGalleryRotate({
				"imageWidth"                : 640,
		                "imageHeight"               : 365,
		                "appHeader"                 : "no",
		                "titles"                    : "yes",
		                "captions"                  : "yes",
		                "playPause"                 : "no",
		                "mainControls"              : "yes",
		                "links"                     : "no",
		                "contMoreLink"              : "no",
		                "contMoreLinkText"          : "Read More",
		                "descMoreLink"              : "no",
		                "descMoreLinkText"          : "Description Read More",
		                "bullets"                   : "no",
		                "bulletControls"            : "no",
		                "thumbnails"                : "no",
		                "thumbnailWidth"            : 122,
		                "thumbnailHeight"           : 53,
		                "thumbnailSlideDirection"   : "horizontal", // horizontal or vertical
		                "hoverPause"                : "yes",
		                "transitionType"            : "fade", // fade or slide
		                "transitionSpeed"           : 3,
		                "onImageLoad"               : function() { },
		                "allImagesLoaded"           : function() { },
		                "onThumbnailLoad"           : function() { },
		                "allThumbnailsLoaded"       : function() { },
		                "onTransitionStart"         : function() { },
		                "onTransitionEnd"           : function() { },
		                "allLoaded"                 : function() { 
		                		//Add a div inside of the gallery desc for the special border-radius
		                		if( $('#hp-slideshow .rs-photo-gallery-desc').length > 0 ){
			                		var descHtml = $('#hp-slideshow .rs-photo-gallery-desc').html();
			                		descHtml = "
" + descHtml + "";
						$('#hp-slideshow .rs-photo-gallery-desc').html(descHtml);
					}

					//Add the chevron classes to the back and next arrows
					if( $('#hp-slideshow .rs-photo-gallery-control').length > 0 ){
						$('#hp-slideshow .rs-photo-gallery-control.back').addClass("icon-font icon-chevron-left");
						$('#hp-slideshow .rs-photo-gallery-control.next').addClass("icon-font icon-chevron-right");
					}
		                }
			});
		}
	}

	function appView(){
		//DOC dont add app into accordion if it doesnt have a header
		$("#hp-content div.ui-widget.app").each(function() {
			if($(".ui-widget-header", this).hasClass("ui-helper-hidden") || jQuery.trim($(".ui-widget-header h1", this).text()) == "") {
				$(this).addClass("no-header");
			}else{
				if($(".ui-widget-header span.icon-font", this).length <= 0){
					$(".ui-widget-header", this).append('');
				}
			}
		});

		//DOC make homepage apps collapsible for small mobile devices
		if(!$("#hp-content div.ui-widget.app.active").length){
			$("#hp-content div.ui-widget.app:first").addClass("active");
			changeAccordionIcon('#hp-content div.ui-widget.app:first', 'icon-chevron-right', 'icon-chevron-down');
		}

		//DOC accordion header click
		$("#hp-content div.ui-widget-header").off('click');
		$("#hp-content div.ui-widget-header").click(function(){
			console.log("clicked");
			if( $(this).parent().hasClass("active") ){ //If they click the same header again it will hide
				//Reset the old accordion
				$(this).parent().removeClass("active");
				changeAccordionIcon(this, "icon-chevron-down", "icon-chevron-right");
			}else{
				var clickedApp = $(this).parent().parent().attr("id");

				//Reset the old accordion
				changeAccordionIcon("#hp-content div.ui-widget.app.active", "icon-chevron-down", "icon-chevron-right");
				$("#hp-content div.ui-widget.app.active").removeClass("active");

				//Setup the new accordion
				$(this).parent().addClass("active");
				changeAccordionIcon(this, "icon-chevron-right", "icon-chevron-down");
				smoothScroll("#" + clickedApp, 3);
			}
		});
	}	

	/*changeAccordionIcon
	**	Use: Will change the accordion dropdown arrows.
	**	Param(s): obj - a jQuery Object, Class, or ID that acts as the parent to filter under
			remove - the class that will be removed
			add - the class that will be added
	**
	*/
	function changeAccordionIcon(obj, remove, add){
		$("span.icon-font", obj)
		.removeClass(remove)
		.addClass(add);
	}

	/*
	** smoothScroll
	** Use: This will smoothly scroll to the id that was passed to the function.
	** Return: N/A
	** Parameter(s): to (jQuery "#id") The id of the section to be scolled to.
	** 		offset (int) The amount of space you want to be left on the top of the element after scroll (in a whole number int value)
	*/
	function smoothScroll( to, offset ){
	    if( to.indexOf("#") < 0 ){ to = "#" + to; } //makes sure we have a valid id accessor string
	    var target = $(to);

	    if (target.length) {
	        $('html,body').animate({
	            scrollTop: target.offset().top - offset //this offset accounts for the fixed menu on the top
	        }, 800, function(){
	            $("html, body").stop(true, false);
	        });
	        return false;
	    }
	}

	function useChannelForSection(){
		//DOC use channel name for pagelistnavigation header if one is not present
		if(!$("div.sp.column.one .ui-widget-header h1").length > 0) {
			$("div.sp.column.one .ui-widget-header").append("
");
		}
        }

