$(document).ready(function(){
   var beaconURL='https://analytics.schoolwires.com/analytics.asmx/Insert?AccountNumber=NvhkT8QiDFXtT2nLIPK4lA%3d%3d&SessionID=0de1ea47-14e2-4423-adf6-2ac0e985dd24&SiteID=4&ChannelID=0&SectionID=0&PageID=1&HitDate=4%2f19%2f2020+1%3a41%3a27+PM&Browser=Unknown+0.0&OS=Unknown&IPAddress=10.61.83.228';
