function getAction(link, action) {
    switch(action) {
        case 'Tab':
            return 'target="_blank"';
        case 'Window':
            return `onclick="window.open('`+link+`', 
                'newwindow', 
                'width=600,height=600'); 
                return false;"`;
        case 'Current': 
            return '';
    }
}

