div.script:
$(document).ready(function () {
    $("#daba59f8-37f3-429a-aa66-d2ae76f55cbf").owlCarousel({
        navigation: false,
        pagination : false,
        autoPlay: getAutoPlayValue(),
        transitionStyle : "fade",
        items: '10',
        mouseDrag: false,
        singleItem: true});

    function getAutoPlayValue() {
        var autoPlayValue = 3000;
        if (autoPlayValue == 0) {
            autoPlayValue = false;
        }
        return 5 > 1 ? autoPlayValue : false;
    }
});

$(document).ready(function () {
    $("#60ab572d-a7f2-49d8-93ee-cdd19d3d87f7").owlCarousel({
        navigation: true, // Show next and prev buttons
        navigationText: [
             "",
             ""
             ],
        items: '10',
        autoPlay : getAutoPlayValue(),

        mouseDrag: false,
        pagination:true,
        paginationNumbers: false,
        responsive:true,
        singleItem: true});

    $('div.promotional-carousel .owl-controls').on('click', function () {
        $('#60ab572d-a7f2-49d8-93ee-cdd19d3d87f7').trigger('owl.stop')
    })

    $('div.promotional-carousel').keyup(function (e) {
        if (e.keyCode == 9 || e.which == 9) {
            $('#60ab572d-a7f2-49d8-93ee-cdd19d3d87f7').trigger('owl.stop');
        }
    });

    // Prevent default on click of owl pagination
    $('.owl-page').on('click', '[href]', function(event){
        event.preventDefault();
    });

    function getAutoPlayValue() {
        var autoPlayValue = 5000;
        if (autoPlayValue == 0) {
            autoPlayValue = false;
        }
        return 5 > 1 ? autoPlayValue : false;
    }

});

jQuery(document).ready(function () {
    jQuery('#75fafbf2-2740-4105-9fa0-62309ef179c3').hide();
    jQuery('#75fafbf2-2740-4105-9fa0-62309ef179c3').listSolrResults({
        currentPagePath: '/content/schools/itca/en',
        label: 'News',
        listFrom: 'solr',
        limit: '3',
        listType: 'news',
        listOrder: 'desc',
        stateAbbr: '',
        viewAllText: 'View All',
        viewAllUrl: '/news.html',
        viewAllTitle: 'View All',
        uuid: '75fafbf2-2740-4105-9fa0-62309ef179c3',
        isEditMode: 'false',
        paginate: 'false',
        entriesPerPage: '10'
    });
});

