        window.resizeMiniCalendar20200419014929800 = function () {
        var calId = 'miniCal20200419014929800';
        var width = $('#' + calId).width();
        var calYearId = 'miniCalYear20200419014929800';
        var calMonthFullId = 'miniCalMonthFull20200419014929800';
        var calMonthShortId = 'miniCalMonthShort20200419014929800';
        var calGoToCalFullId = 'miniCalGotToCalFull20200419014929800';
        var calGoToCalShortId = 'miniCalGoToCalShort20200419014929800';
        if(width < 250) {
            $('#' + calYearId).hide();
            $('#' + calGoToCalFullId).hide();
            $('#' + calGoToCalShortId).show();
        }
        else {
            $('#' + calYearId).show();
            $('#' + calGoToCalFullId).show();
            $('#' + calGoToCalShortId).hide();
        }
        if(width < 170) {
                $('#' + calMonthFullId).hide();
                $('#' + calMonthShortId).show();                                       
        }
        else {
                $('#' + calMonthFullId).show();
                $('#' + calMonthShortId).hide();                                       
        }
    }               
}

