        window.resizeMiniCalendar20200419015144389 = function () {
        var calId = 'miniCal20200419015144389';
        var width = $('#' + calId).width();
        var calYearId = 'miniCalYear20200419015144389';
        var calMonthFullId = 'miniCalMonthFull20200419015144389';
        var calMonthShortId = 'miniCalMonthShort20200419015144389';
        var calGoToCalFullId = 'miniCalGotToCalFull20200419015144389';
        var calGoToCalShortId = 'miniCalGoToCalShort20200419015144389';
        if(width < 250) {
            $('#' + calYearId).hide();
            $('#' + calGoToCalFullId).hide();
            $('#' + calGoToCalShortId).show();
        }
        else {
            $('#' + calYearId).show();
            $('#' + calGoToCalFullId).show();
            $('#' + calGoToCalShortId).hide();
        }
        if(width < 170) {
                $('#' + calMonthFullId).hide();
                $('#' + calMonthShortId).show();                                       
        }
        else {
                $('#' + calMonthFullId).show();
                $('#' + calMonthShortId).hide();                                       
        }
    }               
}

