    function streamingVideoBackground(){
                var videoVendor = "Vimeo";
                var mobilePhoto = "/cms/lib/IL01904746/Centricity/Template/GlobalAssets/images///background_image/LCC.jpg";

                $("#hp-multimedia-video").fullScreenRotator({
                    "videoSource" : videoVendor, // OPTIONS ARE: youtube, vimeo
                    "videoID": "365061479", // YouTube and Vimeo ids are set as default in the script
                    //"videoID": "139702926",
                    "fullScreenBreakpoints" : [960, 768, 640, 480, 320], // OPTIONS ARE [960, 768, 640, 480, 320]
                    "showControls" : true,
                    "showProgressBar" : false, // CURRENTLY NOT IN USE
                    "progressBarType" : "line", // CURRENTLY NOT IN USE BUT OPTIONS ARE: line, circle
                    "progressBarCircleWidth": 70, // pixels
                    "progressBarCircleHeight": 70, // pixels
                    "progressBarStrokeWidth": 10, // pixels
                    "progressBarStrokeColor": "#FFF",
                    "showMobileBackgroundPhoto" : true,
                    "showMobileBackgroundPhoto" : false,
                    "mobileBackgroundPhoto" : mobilePhoto,
                    "mobileBackgroundPhotoBreakpoint" : 768, // OPTIONS ARE 768, 640, 480, 320
                    "onReady" : function() { 

                        //WRAP CONTROLS
                        //$(".cs-fullscreen-video-button").wrapAll("
");

                        //MOVE CONTROLS
                        $(".cs-fullscreen-video-buttons").appendTo("#hp-below-content .video-controls");

                        //VIDEO BUTTON INTERACTION
                        $(".cs-fullscreen-video-button.play-button").attr("aria-label", "Play Background Video");
                        $(".cs-fullscreen-video-button.pause-button").attr("aria-label", "Pause Background Video");

                        $("#hp-below-content").on("click keydown", ".cs-fullscreen-video-button.play-button", function(e) {
                            setTimeout(function () {
                                $(".cs-fullscreen-video-button.pause-button").focus();
                            }, 100); 
                        });
                        $("#hp-below-content").on("click keydown", ".cs-fullscreen-video-button.pause-button", function(e) {
                                setTimeout(function () {
                                    $(".cs-fullscreen-video-button.play-button").focus();
                                }, 100);
                        });

                    },
                    "onStateChange" : function() { }
                });
    }

