		        	}	*/
                    $(".video-controls").show();
			break;
			case "768":
				if($("#hp-multimedia-outer iframe").length) {
					player.api("pause");
					$("#pause-button, #pause-button2").hide();
					$("#play-button, #play-button2").show();
				}
			break;
			case "640":

			break;
			case "480":

			break;
			case "320":

			break;
		}
	}	

