	function hpHeight(){
		/* Edited by Brenton Kelly - 9/30/2015 - Case: 00127455 */
		var windowHeight = $(window).height();
		var hpContentH = $("#hp-content-top").height() + ($("#hp-content-bottom").height() + 116); // 116 IS BOTTOM POSITION OF HP-CONTENT-BOTTOM
		if((windowHeight - 88) <= (hpContentH + 20)) { // 88 IS CHANNEL BAR AND STICKY FOOTER ADDED TOGETHER AND ADD 20 FOR SOME TOP PADDING
			$('#hp-content .inner').height(hpContentH + 20); // ADD 20 FOR SOME TOP PADDING
			if(csGetBreakPoint() == "desktop") {
				$('#hp-content .inner').css("margin-bottom", "36px"); // 36 IS HEIGHT OF STICKY FOOTER
			} else {
				$('#hp-content .inner').css("margin-bottom", "0px");
			}
		} else {
			$('#hp-content .inner').height(windowHeight - 88);
			$('#hp-content .inner').css("margin-bottom", "0px");
		}
	}

