body.script:
  $(document).ready(function() {
    $('.search-results').hide();

    $("#q").on("keyup", function(e){

      if($.trim(this.value).length) {
        $(".search-results").fadeIn("fast");
      }else{
        $(".search-results").fadeOut("fast");
      }
    });

    var pageTemplate = '
' +
      '
' +
        '
'+
        '{{#page_link}}{{/page_link}}'+
        '{{^page_link}}{{/page_link}}'+
        '{{{_highlightResult.name.value}}}'+
        '
{{{_highlightResult.content_summary.value}}}'+
      ''+
    '';

    var livefeedTemplate = '
' +
      '
' +
        '
{{{_highlightResult.author_name.value}}}'+
        '
{{{_highlightResult.status.value}}}'+
      ''+
    '';

    var articleTemplate = '
' +
      '
' +
        ''+
      '' +
      '
' +
        '
{{{_highlightResult.title.value}}}'+
        '
{{{_highlightResult.content_summary.value}}}'+
      ''+
    '';

    var eventTemplate = '
' +
      '
' +
        '
{{{_highlightResult.title.value}}}'+
        '
{{{_highlightResult.when.value}}}'+
        '
{{{_highlightResult.where.value}}}'+
      ''+
    '';

    var scoreTemplate = '
' +
      '
' +
        '
{{{_highlightResult.away_team.value}}}{{{away_score}}}    {{{home_score}}}{{{_highlightResult.home_team.value}}}'+
        '
{{{_highlightResult.when.value}}}'+
        '
{{{_highlightResult.where.value}}}'+
        '
{{{_highlightResult.description.value}}}'+
      ''+
    '';

    var staffTemplate = '
' +
      '
' +
        ''+
      '' +
      '
' +
        '
{{{_highlightResult.full_name.value}}}'+
        '
'+
          ''+
            '{{{_highlightResult.title.value}}}'+
            '
'+
            '{{{_highlightResult.department.value}}}'+
            '
'+
            '{{{phone_number}}}'+
            '
'+
            '{{{email}}}'+
          ''+
        ''+
      ''+
    '';

    var documentTemplate = '
' +
      '
' +
        '
'+
          '{{{_highlightResult.name.value}}} (DOC)'+
        ''+
        '
'+
          'File format:  '+
          '{{{_highlightResult.file_extension.value}}}'+
        ''+
      ''+
    '';

    var delay = (function(){
      var timer = 0;
      return function(callback, ms){
        clearTimeout (timer);
        timer = setTimeout(callback, ms);
      };
    })();

    var search_page = instantsearch({
      searchFunction: function(helper) {
      if (helper.state.query === '') {
        return;
      }
      delay(function(){
      }, 1000 );
      helper.search();
      },
      appId: 'UGFXPYTIF9',
      apiKey: '85b82754bbcc3e3d14315ce353901c79',
      indexName: 'ApptegyCms_Page_production',
      searchParameters: {
        disjunctiveFacets: ['organization', 'organization_alias']
      },
    });

    var search_live_feed = instantsearch({
      searchFunction: function(helper) {
      if (helper.state.query === '') {
        return;
      }
      delay(function(){
      }, 1000 );
      helper.search();
      },
      appId: 'UGFXPYTIF9',
      apiKey: '85b82754bbcc3e3d14315ce353901c79',
      indexName: 'LiveFeed_production',
      searchParameters: {
        disjunctiveFacets: ['organization']
      },
    });

    var search_articles = instantsearch({
      searchFunction: function(helper) {
      if (helper.state.query === '') {
        return;
      }
      delay(function(){
      }, 1000 );
      helper.search();
      },
      appId: 'UGFXPYTIF9',
      apiKey: '85b82754bbcc3e3d14315ce353901c79',
      indexName: 'Article_production',
      searchParameters: {
        disjunctiveFacets: ['organization']
      },
    });

    var search_events = instantsearch({
      searchFunction: function(helper) {
      if (helper.state.query === '') {
        return;
      }
      delay(function(){
      }, 1000 );
      helper.search();
      },
      appId: 'UGFXPYTIF9',
      apiKey: '85b82754bbcc3e3d14315ce353901c79',
      indexName: 'Event_production',
      searchParameters: {
        disjunctiveFacets: ['organization']
      },
    });

    var search_scores = instantsearch({
      searchFunction: function(helper) {
      if (helper.state.query === '') {
        return;
      }
      delay(function(){
      }, 1000 );
      helper.search();
      },
      appId: 'UGFXPYTIF9',
      apiKey: '85b82754bbcc3e3d14315ce353901c79',
      indexName: 'ScoresSchedule_production',
      searchParameters: {
        disjunctiveFacets: ['organization']
      },
    });

    var search_documents = instantsearch({
      searchFunction: function(helper) {
      if (helper.state.query === '') {
        return;
      }
      delay(function(){
      }, 1000 );
      helper.search();
      },
      appId: 'UGFXPYTIF9',
      apiKey: '85b82754bbcc3e3d14315ce353901c79',
      indexName: 'Asset_production',
      searchParameters: {
        disjunctiveFacets: ['organization']
      },
    });

    var search_staff = instantsearch({
      searchFunction: function(helper) {
      if (helper.state.query === '') {
        return;
      }
      delay(function(){
      }, 1000 );
      helper.search();
      },
      appId: 'UGFXPYTIF9',
      apiKey: '85b82754bbcc3e3d14315ce353901c79',
      indexName: 'Directory_production',
      searchParameters: {
        disjunctiveFacets: ['organization']
      },
    });


    search_page.addWidget(
      instantsearch.widgets.searchBox({
        container: '#q',
        autofocus: false,
      })
    );

    search_live_feed.addWidget(
      instantsearch.widgets.searchBox({
        container: '#q',
        autofocus: false,
      })
    );

    search_articles.addWidget(
      instantsearch.widgets.searchBox({
        container: '#q',
        autofocus: false
      })
    );

    search_events.addWidget(
      instantsearch.widgets.searchBox({
        container: '#q',
        autofocus: false
      })
    );

    search_scores.addWidget(
      instantsearch.widgets.searchBox({
        container: '#q',
        autofocus: false
      })
    );

    search_documents.addWidget(
      instantsearch.widgets.searchBox({
        container: '#q',
        autofocus: false
      })
    );

    search_staff.addWidget(
      instantsearch.widgets.searchBox({
        container: '#q',
        autofocus: false
      })
    );

    search_page.addWidget({
      init: function(args) {
        args.helper.addDisjunctiveFacetRefinement(
          'organization', gon.organization
        );
      }
    });

    search_live_feed.addWidget({
      init: function(args) {
        args.helper.addDisjunctiveFacetRefinement(
          'organization', gon.organization
        );
      }
    });

    search_articles.addWidget({
      init: function(args) {
        args.helper.addDisjunctiveFacetRefinement(
          'organization', gon.organization
        );
      }
