    $(document).ready(function () {
        $(".calendarDayEvent, .calendarDayEmpty").click(function (event) {
            GetDayEvents(this)
        });
    });

    function GetDayEvents(element) {
        var currentdate = $(element).find("input[type=hidden]").val();
        $.ajax({
            type: "POST",
            dataType: "json",
            data: { "F": "GetDayEvents", "CurrentDate": currentdate },
            url: "/ajax/events/ajax.aspx",
            success: function (o) {
                if (!o.Success) return;

                var html = "";

                $("#DayEvents").html("");

                html += "
" + o.CurrentDate + " Events";

                if (o.EventsResponse.length != 0) {
                    html += "";
                    for (var i = 0; i < o.EventsResponse.length; i++) {
                        var event = o.EventsResponse[i];
                        html += "
";
                        html += "
";
                        html += "" + event.EventName + "";
                        if (event.EventLocation != null) {
                            html += "
" + event.EventLocation + "";
                        }
                        html += "
" + event.EventTime + "";
                    }
                    html += "";
                } else {
                    html += "
There are no events on this day.";
                }
                $("#DayEvents").append(html);
            }
        });
    }

    function GetMonth(month, year) {
        var EventMemberLoggedIn = 'False' == true ? 1 : 0
        var AccessLevelId = 0

        $.ajax({
            type: "POST",
            dataType: "json",
            data: { "F": "GetMonthEvents", "Month": month, "Year": year, "EventMemberLoggedIn" : EventMemberLoggedIn, "AccessLevelId" : AccessLevelId },
            url: "/ajax/events/ajax.aspx",
            success: function (o) {
                if (!o.Success) return;

                var html = "";
                $("#MonthCalendar").html("");
                $("#currentMonth").html("
" + o.Month + "" + o.Year + "
 ")

                if (o.MonthResponse.length != 0) {
                    html += "
";
                    html += "   ";
                    html += "       
";
                    html += "           Sunday";
                    html += "           Monday";
                    html += "           Tuesday";
                    html += "           Wednesday";
                    html += "           Thursday";
                    html += "           Friday";
                    html += "           Saturday";
                    html += "       ";
                    html += "       
";

                    for (var i = 0; i < o.MonthResponse.length; i++) {
                        var dayData = o.MonthResponse[i];
                        html += "";
                        html += "   ";
                
                        html += "   
";
                        html += "   
";
                        html += "   
";
                        html += "       
" + dayData.DayNum + "";
                        html += "   ";

                        if(i % 7 == 6){
                            html += "
"
                        }
                    }

                    html += "";
                }
                $("#MonthCalendar").append(html);

                $("#monthNav").html("");
                html = "" + o.PrevMonthName + "";
                html += "View Full Calendar";
                html += "" + o.NextMonthName + "";
                
                $("#monthNav").append(html)
            }
        });
    }
