window.onload = function(){
    function checkCookieName(name)
    {
      var match = document.cookie.match(new RegExp('(^| )' + name + '=([^;]+)'));
      if(match !== null){
          return match[2];
      }
      else{
          return;
      }
   }
    if(checkCookieName('cookieconsent_status')=="deny"){
    fbq('consent', 'revoke');
}else if(checkCookieName('cookieconsent_status')=="allow"){
    fbq('consent', 'grant');
}
}
