link.script:
kQuery(function($){
        var recorded_plays = [];

        plyr.setup();

        $(document).on('playing', function(event) {
            if (typeof event.detail !== 'undefined' && typeof event.detail.plyr !== 'undefined') {
                var plyr = event.detail.plyr;

                // If they've played over 3 seconds, then consider it played
                // This is the same timing convention used by Facebook, Instagram, and Twitter
                setTimeout(function() {
                    if (!plyr.isPaused()) {
                        var media = $(plyr.getMedia());

                        var category = media.data('category');
                        var action = 'Play ' + plyr.getType();

                        var title = media.data('title') || '';
                        var id = parseInt(media.data('media-id'), 10) || 0;

                        if (recorded_plays.indexOf(title) === -1) {
                            recorded_plays.push(title);

                            if (typeof window.GoogleAnalyticsObject !== 'undefined' && typeof window[window.GoogleAnalyticsObject] !== 'undefined') {
                                window[window.GoogleAnalyticsObject]('send', 'event', category, action, title, id);
                            }
                            else if (typeof _gaq !== 'undefined' && typeof _gat !== 'undefined') {
                                if (_gat._getTrackers().length) {
                                    _gaq.push(function () {
                                        var tracker = _gat._getTrackers()[0];
                                        tracker._trackEvent(category, action, title, id);
                                    });
                                }
                            }
                        }
                    }
                }, 3000);
            }
        });
    });

div.script:
jQuery(document).ready( function() {
	jQuery(document).keyup(function(e) {
		if(e.keyCode === 27) {
			jQuery( "div.uk-button-dropdown.uk-open" ).removeClass('uk-open');
		}
		if( e.keyCode === 13) {
			jQuery('.uk-nav-offcanvas > li')
			.first()
			.find('a')
			.first()
			.focus();
		}
	});

	jQuery( "div.uk-button-dropdown" ).focus(function() {
			const focusOnFirst = !jQuery(this).hasClass("uk-open");

			jQuery(this)
				.addClass( "uk-open" )
				.attr("aria-expanded","true");

			jQuery("div.uk-button-dropdown").not(this)
				.removeClass("uk-open")
				.attr("aria-expanded","false");

			if(focusOnFirst) {
				jQuery(this)
					.find("ul.uk-nav li a")
					.first()
					.focus();
			}
			else {
				jQuery(this)
					.removeClass("uk-open")
					.attr("aria-expanded", "false");
			}

	});

	jQuery( "ul.uk-nav li:last-child a").blur(function(){
		jQuery(this)
			.closest('div.uk-button-dropdown')
			.removeClass( "uk-open" )
			.attr("aria-expanded","false");
	});

	// ACCORDION FOCUS
	jQuery('.uk-accordion-content a').focus(function() {
		// find the parent accordion of this link, and if it's not already active, then expand it
		jQuery(this)
			.closest('.uk-accordion-content')
			.parent()
			.prev()
			.not('.uk-active')
			.trigger('click');
	});

	jQuery('.uk-accordion-content:last-child a:last-child').blur(function() {
		jQuery(this)
			.closest('.uk-accordion-content')
			.parent()
			.prev()
			.trigger('click');
	});

});

function TranslateTo(language)
{
  var currentURL = window.location.href;

  if (language.match('original')) {
    currentURL = $.getUrlVar('u');
    parent.location = unescape(currentURL);
  }
  else {
    if (currentURL.indexOf('translate.google.com') > 0)
      currentURL = unescape($.getUrlVar('u'));
      parent.location='http://translate.google.com/translate?hl=' + language + '&sl=auto&tl=' + language + '&u=' + encodeURIComponent(currentURL);
    }
  }

  (function($) {
    $(document).ready(function(){

      $.extend({
        getUrlVars: function(){
        var vars = [], hash;
        var hashes = window.location.href.slice(window.location.href.indexOf('?') + 1).split('&');
        for(var i = 0; i < hashes.length; i++)
        {
          hash = hashes[i].split('=');
          vars.push(hash[0]);
          vars[hash[0]] = hash[1];
        }
        return vars;
      },
      getUrlVar: function(name){
        return $.getUrlVars()[name];
      }
    });

    if ($(".languageDropdown").length > 0) {
      var currentURL = window.location.href;
        if (currentURL.indexOf('translate.google.com') > 0)
          document.getElementById('gtl_original').style.display = 'inline-block';
        else
          document.getElementById('gtl_original').style.display = 'none';
    }
  });
})(jQuery);

