        window.resizeMiniCalendar20200419020622420 = function () {
        var calId = 'miniCal20200419020622420';
        var width = $('#' + calId).width();
        var calYearId = 'miniCalYear20200419020622420';
        var calMonthFullId = 'miniCalMonthFull20200419020622420';
        var calMonthShortId = 'miniCalMonthShort20200419020622420';
        var calGoToCalFullId = 'miniCalGotToCalFull20200419020622420';
        var calGoToCalShortId = 'miniCalGoToCalShort20200419020622420';
        if(width < 250) {
            $('#' + calYearId).hide();
            $('#' + calGoToCalFullId).hide();
            $('#' + calGoToCalShortId).show();
        }
        else {
            $('#' + calYearId).show();
            $('#' + calGoToCalFullId).show();
            $('#' + calGoToCalShortId).hide();
        }
        if(width < 170) {
                $('#' + calMonthFullId).hide();
                $('#' + calMonthShortId).show();                                       
        }
        else {
                $('#' + calMonthFullId).show();
                $('#' + calMonthShortId).hide();                                       
        }
    }               
}

