div.script:
	function googleTranslateElementInit() {
	  new google.translate.TranslateElement({
			pageLanguage: 'en',
			includedLanguages: 'af,sq,ar,hy,az,eu,be,bn,bs,bg,ca,ny,zh-CN,zh-TW,hr,cs,da,tl,fr,gl,de,el,gu,ht,iw,hi,ig,id,it,ja,kn,kk,ko,mk,ms,mt,mn,my,ne,fa,pl,pt-BR,pt-PT,pa,ro,ru,sr,st,so,es,es-419,su,sw,ta,te,th,tr,uk,ur,vi,yo,zu', 
		}, 'google_translate_element');

		// Replace the select element
		jcf.setOptions('Select', {

			wrapNative: false
		});
		jcf.replaceAll();
		jQuery('#google_translate_element').bind('DOMNodeInserted', function(event) {
            var firstOption = jQuery('.goog-te-combo option').eq(0);
            if (firstOption.html() === 'Select Language') {
                firstOption.addClass('hide').html('Languages');
            };
        });
		// Place the text in the jcf select element since
		// it's not happening automatically
		jQuery("#google_translate_element .jcf-select-text span").html("Languages");
	}

jQuery(document).ready(function() {
	jQuery('.arts-blog__slick').slick();
});
jQuery(window).on('bp:xs bp:sm', function() {
	// jQuery(".arts-blog").appendTo(jQuery(".arts-blog--mobile"));
	jQuery(".arts-blog--feature").removeClass("arts-blog--desktop");
	jQuery(".arts-blog--feature").addClass("arts-blog--mobile");
	jQuery('.arts-blog__slick').slick('setPosition');
});
jQuery(window).on('bp:md bp:lg', function() { 
	// jQuery(".arts-blog").appendTo(jQuery(".arts-blog--desktop"));
	jQuery(".arts-blog--feature").addClass("arts-blog--desktop");
	jQuery(".arts-blog--feature").removeClass("arts-blog--mobile");
	jQuery('.arts-blog__slick').slick('setPosition');
});

jQuery(document).ready(function() {
	// Replace the select element
	jcf.replaceAll();
		jcf.setOptions('select', {
			wrapNative: false
		});
});

jQuery(document).ready(function() {
	jQuery(window).breakpointEvents({
			defaultBreakpoint: 'xs',
			breakpoints: {
				'sm' : 750,
				'md' : 970,
				'lg' : 1170
			},
			modelSelector: '.breakpoint-events-watch'
		});
});
// These will go in main.js but kept them here for troubleshooting for now
jQuery(window).on('bp:xs', function TopNavMobile() { 
	jQuery(".top-nav__item").appendTo(jQuery("#main-menu__items"));
	jQuery(".top-nav__item").addClass("main-menu__item");
	jQuery(".top-nav__link").addClass("main-menu__item-link");
});
jQuery(window).on('bp:sm bp:md bp:lg', function TopNavDesktop() { 
	jQuery(".top-nav__item").prependTo(jQuery(".top-nav__menu"));
	jQuery(".top-nav__item").removeClass("main-menu__item");
	jQuery(".top-nav__link").removeClass("main-menu__item-link");
});
