// Bug 76422

		window.addEventListener('load', myInit, true);

		function myInit(){

		$('#datepicker8 a.ui-datepicker-prev').keydown(function(e){
	    	if(e.which == 13){ // enter key
	    		e.preventDefault();
	    		$('#datepicker8 a.ui-datepicker-prev').click();
	    		handlePrevClick8();
	    	}
	    });
	    $('#datepicker8 a.ui-datepicker-next').keydown(function(e){
	    	if(e.which == 13){ // enter key
	    		e.preventDefault();
	    		$('#datepicker8 a.ui-datepicker-next').click();
	    		handleNextClick8();
	    	}
	    });


		function handlePrevClick8(){
			setTimeout(function () {
				triggerClick8();
			    $('#datepicker8').find('.ui-datepicker-prev').focus();
			}, 0);
		}
		function handleNextClick8(){
			setTimeout(function () {
				triggerClick8();
			    $('#datepicker8').find('.ui-datepicker-next').focus();
			}, 0);
		}
		function triggerClick8(){
			prev = $('#datepicker8').find('.ui-datepicker-prev');
			next = $('#datepicker8').find('.ui-datepicker-next');
			prev.attr('tabindex','0');
			next.attr('tabindex','0');

			$(next).on('click', handleNextClick8);
			$(prev).on('click', handlePrevClick8);
		}

		};



