	var plusSVG = 'Plus SVG',
	    homeSVG = 'home-svgHOME',
	    arrowSVG = 'arrowArrow SVG',
	    moreSVG = 'more-arrowMore SVG',
	    shadowSVG = 'right-shadowrightshadowleft-shadowleftshadow';
	var vpw = $(window).width();
	var appViewSet = 1;

	function sortSchools(list){
		var csElemSchools = [], csK8Schools = [], csMiddleSchools = [], csHighSchools = [], csSpecialSchools = [], csAdministrativeSchools = [];
		$(list).each(function(k, v) {
        	if(list[k].schoolVisible == 'true' || list[k].schoolVisible == 'TRUE'){
                var csSchoolName = list[k].schoolName, csSchoolURL = list[k].schoolUrl, csSchoolType = list[k].schoolType;
                var csPackedSchool = [csSchoolName, csSchoolURL];
                // ELEMENTARY SCHOOL
                if(csSchoolType.indexOf('Elementary') >= 0) {
                    csElemSchools.push(csPackedSchool);
                }

                // K8 SCHOOL
                if(csSchoolType.indexOf('K-8') >= 0) {
                    csK8Schools.push(csPackedSchool);
                }

                // MIDDLE SCHOOL
                if(csSchoolType.indexOf('Middle') >= 0) {
                    csMiddleSchools.push(csPackedSchool);
                }

                // HIGH SCHOOL
                if(csSchoolType.indexOf('High') >= 0) {
                    csHighSchools.push(csPackedSchool);
                }

                // SPECIAL SCHOOL
                if(csSchoolType.indexOf('Special') >= 0) {
                    csSpecialSchools.push(csPackedSchool);
                }

                // ADMINISTRATIVE SCHOOL
                else {
                    // DONT INCLUDE PARENTS, STUDENTS, STAFF, ESPANOL, OR ARABIC SITES
                    csAdministrativeSchools.push(csPackedSchool);
                }
            }
		});//End each
        /*
        $(".schoollist .sw-dropdown-list li").each(function() {
			var csSchoolName = $("a", this).text(), csSchoolURL = $("a", this).attr("href");
			var csPackedSchool = [csSchoolName.replace(/Elementary|K-8|Middle|High|Special|Full|Two/i, ""), csSchoolURL];	// Commented out this line and added the line below by Lyndsay - Case 00351730
            //var csPackedSchool = [csSchoolName, csSchoolURL];

