		// BUILD ELEMENTARY SCHOOL LIST
		var elementarySchools = "";
		for(var e in csElemSchools) {
			elementarySchools += "1," + csElemSchools[e][0] + "," + csElemSchools[e][1] + ",_self;";
		}

		// BUILD K-8 SCHOOL LIST
		var k8Schools = "";
		for(var k in csK8Schools) {
			k8Schools += "2," + csK8Schools[k][0] + "," + csK8Schools[k][1] + ",_self;";
		}

		// BUILD MIDDLE SCHOOL LIST
		var middleSchools = "";
		for(var m in csMiddleSchools) {
			middleSchools += "3," + csMiddleSchools[m][0] + "," + csMiddleSchools[m][1] + ",_self;";
		}

		// BUILD HIGH SCHOOL LIST
		var highSchools = "";
		for(var g in csHighSchools) {
			highSchools += "4," + csHighSchools[g][0] + "," + csHighSchools[g][1] + ",_self;";
		}

		// BUILD SPECIAL SCHOOL LIST
		var specialSchools = "";
		for(var sp in csSpecialSchools) {
			specialSchools += "5," + csSpecialSchools[sp][0] + "," + csSpecialSchools[sp][1] + ",_self;";
		}

		// BUILD ADMINISTRATIVE SCHOOL LIST
		var administrativeSchools = "";
		for(var a in csAdministrativeSchools) {
			administrativeSchools += "6," + csAdministrativeSchools[a][0] + "," + csAdministrativeSchools[a][1] + ",_self;";
		}

		function buildSchoolList(csElemSchools, csK8Schools, csMiddleSchools, csHighSchools, csSpecialSchools, csAdministrativeSchools) {
			// BUILD ELEMENTARY SCHOOL LIST
			var fullSchoolList = '
';
			var max = Math.ceil(csElemSchools.length / 4) + 1;
			var n = 1;
			for(var i = 0; i < csElemSchools.length; i++) {
				fullSchoolList += '
' + csElemSchools[i][0] + '';

				n++;
				if(n == max) {
					fullSchoolList += '';

					n = 1;
				}
			}

			fullSchoolList += '';

			//BUILD K8 SCHOOL LIST
			fullSchoolList += '
';
			max = Math.ceil(csK8Schools.length / 4) + 1;
			n = 1;
			for(var i = 0; i < csK8Schools.length; i++) {
				fullSchoolList += '
' + csK8Schools[i][0] + '';

				n++;
				if(n == max) {
					fullSchoolList += '';

					n = 1;
				}
			}

			fullSchoolList += '';

			//BUILD MIDDLE SCHOOL LIST
			fullSchoolList += '
';
			max = Math.ceil(csMiddleSchools.length / 4) + 1;
			n = 1;
			for(var i = 0; i < csMiddleSchools.length; i++) {
				fullSchoolList += '
' + csMiddleSchools[i][0] + '';

				n++;
				if(n == max) {
					fullSchoolList += '';

					n = 1;
				}
			}

			fullSchoolList += '';

			//BUILD HIGH SCHOOL LIST
			fullSchoolList += '
';
			max = Math.ceil(csHighSchools.length / 4) + 1;
			n = 1;
			for(var i = 0; i < csHighSchools.length; i++) {
				fullSchoolList += '
' + csHighSchools[i][0] + '';

				n++;
				if(n == max) {
					fullSchoolList += '';

					n = 1;
				}
			}

			fullSchoolList += '';

			//BUILD SPECIAL SCHOOL LIST
			fullSchoolList += '
';
			max = Math.ceil(csSpecialSchools.length / 4) + 1;
			n = 1;
			for(var i = 0; i < csSpecialSchools.length; i++) {
				fullSchoolList += '
' + csSpecialSchools[i][0] + '';

				n++;
				if(n == max) {
					fullSchoolList += '';

					n = 1;
				}
			}

			fullSchoolList += '';

			//BUILD ADMINISTRATIVE SCHOOL LIST
			fullSchoolList += '
';
			max = Math.ceil(csAdministrativeSchools.length / 4) + 1;
			n = 1;
			for(var i = 0; i < csAdministrativeSchools.length; i++) {
				fullSchoolList += '
' + csAdministrativeSchools[i][0] + '';

				n++;
				if(n == max) {
					fullSchoolList += '';

					n = 1;
				}
			}

			fullSchoolList += '';

			// ADD EVERYTHING TO THE DOM
			$("#tab-content-inner").html(fullSchoolList);
		}

		// csElemSchools, csMiddleSchools, csHighSchools, csVirtualSchools, csSummerSchools
		buildSchoolList(csElemSchools, csK8Schools, csMiddleSchools, csHighSchools, csSpecialSchools, csAdministrativeSchools);
		// RESPONSIVE MENU
		$("body").csRsMenu({
			"breakPoint"		: 768, // SYSTEM BREAK POINTS - 768, 640, 480, 320
			"primaryColor"          : "#1A1A1A", // DEFAULT - #1A1A1A
		        "secondaryColor"        : "#0C0C0C", // DEFAULT - #0C0C0C
		        "accentColor"           : "#BABABA", // DEFAULT - #BABABA
			"menuButtonParent"	: "#right-upper",
			"showMenuBtnText"	: "yes",
			"menuBtnText"		: "MENU",
			"showSchools"		: "no",
			"showTranslate"		: "no",
			"translateId"		: "none",
			"showAccount"		: "yes",
			"extraMenuOptions"	: "Elementary Schools;K-8 Schools;Middle Schools;High Schools;Special Schools", // EXTRA MENU HEADING NAMES SEPARATED BY A ;
			"extraMenuItems"	: elementarySchools + k8Schools + middleSchools + highSchools + specialSchools, // EXTRA MENU BUTTONS - STRUCTURE IS: MENU-OPTION-NUMBER, ITEM-NAME, ITEM-URL, ITEM-TARGET;
			"siteID"		: "4"
		});
	}

	function hfAltLink(){
		$('.headlines li').each(function(){
			var link = $('img', this).attr('alt');
			if(link !== undefined ){
				link = link.trim();
				if(link.indexOf('http') > -1 || link.indexOf('www') > -1){
					if(link.indexOf('www') > -1 && link.indexOf('http') == -1){
						$('a', this).attr('href', 'http://'+link);
					} else {
						$('a', this).attr('href', link);
					}
				}
			} else {
				$('img', this).attr('alt', 'Wichita Schools');
			}
		});//End Each
	}

