	function jsMediaQueries(){
		switch(csGetBreakPoint()) {
			case "desktop": 
				appViewReset();
                $('#gb-district a').prepend(homeSVG);
			break;
			case "768":
				newAppView();
                 $('#gb-district a').remove(homeSVG);
			break;
			case "640":
				newAppView();
                 $('#gb-district a').remove(homeSVG);
			break;
			case "480":
				newAppView();
                 $('#gb-district a').remove(homeSVG);
			break;
			case "320":
				newAppView();
                 $('#gb-district a').remove(homeSVG);
			break;
		}
	}

