$(document).ready(function(){
   var beaconURL='https://analytics.schoolwires.com/analytics.asmx/Insert?AccountNumber=BpXw%2feZjW24fXkJGf5b0bg%3d%3d&SessionID=8ff1a082-5c3a-4748-bd26-3f8512961107&SiteID=4&ChannelID=0&SectionID=0&PageID=1&HitDate=4%2f19%2f2020+1%3a15%3a11+PM&Browser=Unknown+0.0&OS=Unknown&IPAddress=10.61.90.186';
   try {
      $.getJSON(beaconURL + '&jsonp=?', function(myData) {});
