	    ally.ready(function() {
		    // Configure an API instance
            const ui = ally.ui({
			    'client': {
				    'baseUrl': 'https://prod.ally.ac',
				    'clientId': 446
			    },
			    'courseId': 's4',
			    'locale': $('html').attr('lang'),
			    'platformName': 'Wcm',  
			    'role': 'anonymous'
		    });
	        // Apply the API to the page body   
            ui.autoUpdate({'domWatch': 250});
    });
