link.script:
function suggest(inputString){

if(inputString.length == 0) {
jQuery('#suggestions').fadeOut();
} else {
//alert("HI");
jQuery('#howdoi').addClass('load');

jQuery.post("/view-html/main/autosuggest.php", {queryString: ""+inputString+""}, function(data){

if(data.length >0) {
jQuery('#suggestions').fadeIn();
jQuery('#suggestionsList').html(data);
jQuery('#howdoi').removeClass('load');
}
});
}



}

 
function fill(thisValue) {


jQuery('#howdoi').val(thisValue);
setTimeout("jQuery('#suggestions').fadeOut();", 600);

setOut = document.getElementById('howdoi').value;


var isContains = setOut.indexOf('!!') > -1;


//window.location.href = setOut
if (isContains) {
setOut = setOut.substring(2);
setTimeout(function() { window.location.href = setOut},300);
 
}
jQuery('#howdoi').val('');


}

