 $(document).observe('dom:loaded',function(){
 try{
 var menu_base = $('landingMainMenu');
 window.panel_menu = new tpcg.ui.PanelMenu(
 menu_base.select('.PanelMenu-InnerTube')[0],
 new tpcg.ui.SlidingWidget( menu_base.select('.Slider-Frame')[0] )
 );

 window.panel_menu.resizeFrame = menu_base.select('.Slider-ClippingArea')[0];

 $$('.LandingMenu-Entry').each(function(menu_link){
 var panel_id = menu_link.id.replace('-Link','');
 var index = window.panel_menu.frame.append( new tpcg.ui.MenuPanel( menu_link, $(panel_id) ) );
 menu_link.observe('click',window.panel_menu.onShowHandler.curry(index).bind(window.panel_menu));
 });

 window.panel_menu.frame.show(0,false);
 window.panel_menu.resize();
 window.panel_menu.collapse(true);

 Event.observe($(window),'resize',window.panel_menu.onResizeHandler.bind(window.panel_menu));
 $('closeLink').observe('click',window.panel_menu.onCloseHandler.bind(window.panel_menu));
 $('closeLink2').observe('click',window.panel_menu.onCloseHandler.bind(window.panel_menu));

 }catch(e){alert(e.toSource());}
 });

 function toggleBillBoard (itemid) {
 // updatecount(itemid); 
 
 
updatecount(itemid);
 
window.billboard_controller.toggle(); 
 }

 
 
 $(document).observe('dom:loaded',function(){

 try{
 window.billboard_controller = new tpcg.ui.Billboard_Controller(
 new tpcg.ui.FadingWidget( $('tpcgBillboard').select('.Billboard-Frame')[0], {ttl:20,duration:0.5} ),
 $('billboardPlayLink')
 );

 var link_click_handler = function(ClickEvent){
window.billboard_controller.pause();
 ClickEvent.stop();
 window.billboard_controller.widget.show(this.panelIndex);
 };
 
 $$('.PanelSwitcher-Link').each(function(link){
 var panel_id = link.id.replace( '-Link', '' );
 var billboard_panel = new tpcg.ui.BillboardPanel(link,$(panel_id));
 billboard_panel.panel.hide();
 link.panelIndex = window.billboard_controller.widget.append(billboard_panel);
 
 link.observe('click', link_click_handler.bind(link) );
 });

 window.billboard_controller.widget.show(0,true);
 if ( window.billboard_controller.widget.panels.length > 1 ){ 
 
 window.billboard_controller.play();
 }else{

 window.billboard_controller.playButton.remove();
 }
 }catch(e){alert(e.toSource());}
 });

 
 
$(document).observe('dom:loaded',function(){
