$(document).observe("dom:loaded", function(){
 emergency_update_toggles = $$('.emergency_update_toggle') || [];
 if(emergency_update_toggles.length > 0 ){
 emergency_update_toggles.each(function(x){
 x.doToggle = function(e){
 Event.stop(e);
 var openClass = 'open';
 var indicatorOpenClass = 'fa-caret-up';
 var indicatorClosedClass = 'fa-caret-down';
 $$('.emergency_update_message').invoke('hide'); 
 openIndicator = this.down('.open_indicator');
 container = $(this.getAttribute('data-toggle'));
 if(container){ 
 if(!container.hasClassName(openClass)){
 container.toggle();
 $$('.emergency_update_toggle').invoke('removeClassName', openClass);
 $$('.emergency_update_message').invoke('removeClassName', openClass); 
 $$('.emergency_update_toggle .open_indicator').invoke('removeClassName', indicatorOpenClass); 
 $$('.emergency_update_toggle .open_indicator').invoke('addClassName', indicatorClosedClass); 
 container.addClassName(openClass);
 this.addClassName(openClass);
 if(openIndicator){
 openIndicator.removeClassName(indicatorClosedClass);
 openIndicator.addClassName(indicatorOpenClass);
 }
 } else {
 $$('.emergency_update_toggle').invoke('removeClassName', openClass);
 $$('.emergency_update_message').invoke('removeClassName', openClass); 
 if(openIndicator){
 openIndicator.removeClassName(indicatorOpenClass);
 openIndicator.addClassName(indicatorClosedClass);
 }
 }
 } 
 }
 x.observe('click', x.doToggle.bind(x) );
 });
 }
 
});

