	$('#dropdown-menu-id-65').dropDownMenu({
		format: 'collapse', 
		speed: 250,
		topLevelCaret: true, // Adds the caret to top level nav items.
		topLevelCaretIconInactive: 'fa-angle-down',
		topLevelCaretIconActive: 'fa-caret-down',
		caretIconInactive: 'fa-angle-down', 
		caretIconActive: 'fa-caret-down',
	});
	
	$('#toggle-menu-65').toggleMenu({
		subMenuCaretInactive: 'fa-angle-down',
		subMenuCaretActive: 'fa-caret-down',
		speed: 250,
	});
	
});

// Expand Type

$('#welcome-message-1731').each(function(){

	var WMexpandTrigger = $(this).find('.welcome-message-expand-trigger');
	
	$(WMexpandTrigger).click(function(evt){
		evt.preventDefault();
		
		var WMactvExpandTrigger = $(this);
		var WMshortContent = $(this).parent().parent().find('.welcome-message-content-inner');
		var WMshortContentHTML = $(WMshortContent).html();
		var WMfullContent =  $(this).parent().parent().find('.welcome-message-content-full');
		var WMfullContentHTML =  $(WMfullContent).html();
		
		$(WMactvExpandTrigger).fadeOut();
				
		$(WMshortContent).html(WMfullContentHTML);
				
		var WMcloseTrigger = $(this).parent().parent().find('.welcome-message-collapse-trigger');
		
		$(WMcloseTrigger).click(function(evt){
			evt.preventDefault();
				$(WMshortContent).html(WMshortContentHTML);
				$(WMactvExpandTrigger).fadeIn();
		});
		
		
	});
});

// breaks links into 2 columns if there's enough room.

// We can draw on some other conclusions too, for example if all the links
// are less than 30 characters, we can fit two columns, but if they are greater, there's
// only room for one column to try and prevent word wrap.

var highlightedLinksBlock = $('#highlighted-action-wrap-1887');

$(highlightedLinksBlock).each(function(){
	
	var hlbBlock = $(this);
	var hlbLinks = $(this).find('.highlighted-action-link');
	
	$(window).on('resize', function () {
		
		var hlbWidth = $(hlbBlock).width();
		
		if ($(hlbBlock).width() > 400) {
			 $(hlbLinks).addClass('highlighted-action-link-split');
		}
		else {
			$(hlbLinks).removeClass('highlighted-action-link-split');
		}
	
	}).resize();
	
});

