        window.resizeMiniCalendar20200419030012130 = function () {
        var calId = 'miniCal20200419030012130';
        var width = $('#' + calId).width();
        var calYearId = 'miniCalYear20200419030012130';
        var calMonthFullId = 'miniCalMonthFull20200419030012130';
        var calMonthShortId = 'miniCalMonthShort20200419030012130';
        var calGoToCalFullId = 'miniCalGotToCalFull20200419030012130';
        var calGoToCalShortId = 'miniCalGoToCalShort20200419030012130';
        if(width < 250) {
            $('#' + calYearId).hide();
            $('#' + calGoToCalFullId).hide();
            $('#' + calGoToCalShortId).show();
        }
        else {
            $('#' + calYearId).show();
            $('#' + calGoToCalFullId).show();
            $('#' + calGoToCalShortId).hide();
        }
        if(width < 170) {
                $('#' + calMonthFullId).hide();
                $('#' + calMonthShortId).show();                                       
        }
        else {
                $('#' + calMonthFullId).show();
                $('#' + calMonthShortId).hide();                                       
        }
    }               
}

