div.script:
function JustFollowThisLinkPlease(href, target) {
    if (!target || target == "" || target == "_self") {
        alert(target);
        document.location = href;
    }
    else {
        window.open(href);
    }
    var e = window.event;
    e.cancelBubble = true;
    if (e.stopPropagation) e.stopPropagation();
    return false;
}

