    $(function() {
    	CreativeTemplate.Init();
    });

    var CreativeTemplate = {
    	// PROPERTIES
        "KeyCodes": { "tab": 9, "enter": 13, "esc": 27, "space": 32, "end": 35, "home": 36, "left": 37, "up": 38, "right": 39, "down": 40 },
        "IsMyViewPage": false, // UPDATES IN SetTemplateProps METHOD

        // METHODS
        "Init": function() {
            // FOR SCOPE
            var _this = this;

            this.SetTemplateProps();
            this.DynamicStyles();
            this.Header();
            this.MyStart();            
            this.ChannelBar();
            this.Body();
            this.UpcomingEvents();
            this.Footer();
            this.Slideshow();
            this.RsMenu();
            this.Search();
            this.JsMediaQueries();

            csGlobalJs.OpenInNewWindowWarning();

            $(window).load(function(){ _this.WindowLoad(); });
            $(window).resize(function(){ _this.WindowResize(); });
            $(window).scroll(function(){ _this.WindowScroll(); _this.StickyHeader(); });
        },

        "SetTemplateProps": function() {
        	// MYVIEW PAGE CHECK
            if($("#pw-body").length) this.IsMyViewPage = true;
        },

        "WindowLoad": function() {
 			$("div.ui-widget.app.navigation li .bullet").each(function() {
            	$(this).insertAfter($(this).siblings("a"));
            });	

            $(".sp-column.one div.ui-widget.app.navigation li").each(function() {
            	if($(this).hasClass("collapsible")) {
                	$("> .bullet", this).click();
                }
            });

            if($(".hp").length && !$("#sw-content-container11 .ui-widget.app.multimedia-gallery .mmg-slide").length) {
            	$(".mmg-description, #gb-scroll-to-content, .mmg-controls, #gb-global-icons-outer").addClass("gb-absolute");
            }
        },

        "WindowResize": function() {
            this.JsMediaQueries();
        },

        "WindowScroll": function() {
        	if($(".hp").length && $("#sw-content-container11 .ui-widget.app.multimedia-gallery .mmg-slide").length) {
                switch(this.GetBreakPoint()) {
                    case "desktop":
                        if($("#gb-emergency-announcement div.ui-widget.app.announcements .ui-articles li").length) {
                    		var scrollBottom = $(window).scrollTop() + $(window).height() - $("#gb-emergency-announcement-outer").outerHeight(true) - $("#gb-channel-list-outer").outerHeight(true) - $("#gb-header-outer").outerHeight(true);;
                        } else {
                    		var scrollBottom = $(window).scrollTop() + $(window).height() - $("#gb-channel-list-outer").outerHeight(true) - $("#gb-header-outer").outerHeight(true);;
                        }

                        if (scrollBottom >= $("#hp-slideshow-outer .mmg-container").height()) {
                           $(".mmg-description, #gb-scroll-to-content, .mmg-controls, #gb-global-icons-outer").addClass("gb-absolute");
                        } else {
                           $(".mmg-description, #gb-scroll-to-content, .mmg-controls, #gb-global-icons-outer").removeClass("gb-absolute");
                        }
                    break;
                    case "768": case "640": case "480": case "320":
                        $(".mmg-description, #gb-scroll-to-content, .mmg-controls, #gb-global-icons-outer").removeClass("gb-absolute");
                    break;
                }
            }
        },

        "StickyHeader": function() {
			if(!this.IsMyViewPage) {
                switch(this.GetBreakPoint()) {
                    case "desktop":
                        var navOffSet = $("header").outerHeight();
                        if ($(window).scrollTop() <= navOffSet) {
                            if(this.HeaderIsSticky) {
                                $("nav").removeClass("fixed-element").removeAttr("style");
                                $("#gb-page").css("padding-top", 0);

                                this.HeaderIsSticky = false;
                            }

                            if($(".sp").length && $(".ui-widget.app.calendar").length) {
                                $(".wcm-controls").css("margin-top", "0px");
                            }
                        } else {
                            $("nav").css("height", $("nav").outerHeight(true));
                            $("#gb-page").css("padding-top", $("nav").outerHeight(true));

                            if(!this.HeaderIsSticky) {
                                $("nav").addClass("fixed-element");

                                this.HeaderIsSticky = true;
                            }

                            if($(".sp").length && $(".ui-widget.app.calendar").length) {
                            	if($(".wcm-controls").hasClass("wcm-stuck")) {
                                	$(".wcm-controls").css("margin-top", $("header").outerHeight(true));
                                }
                            }
                        }

                        $("#sw-maincontent").css({
                            "display": "block",
                            "position": "relative",
                            "top": "-" + $("nav").outerHeight(true) + "px"
                        });
                    break;
                    case "768": case "640": case "480": case "320":
                        if(this.HeaderIsSticky) {
                            $("header").removeClass("fixed-element").removeAttr("style");
                            $("#gb-page").css("padding-top", 0);

                            this.HeaderIsSticky = false;
                        }

                        if($(".sp").length && $(".ui-widget.app.calendar").length) {
                            $(".wcm-controls").css("margin-top", "0px");
                        }
                        $("#sw-maincontent").removeAttr("style");
                    break;
                }
            }
        },

        "JsMediaQueries": function() {
        	var mmgArrays = this.GatherMMGs();

            switch(this.GetBreakPoint()) {
                case "desktop": 
                	$("#sw-mystart-mypasskey").insertAfter($(".cs-mystart-dropdown.translate"));
                    $(".hp #gb-social-media-icons").prependTo($("#hp-slideshow-outer"));
                    $(".sp #gb-social-media-icons").insertAfter($(".sp-breadcrumbs"));

                    if(!$("#hp-slideshow .mmg-description-title span").legnth || !$("#hp-slideshow .mmg-description-caption span").length) {
                        $("#hp-slideshow .mmg-description-title").wrapInner("");
                        $("#hp-slideshow .mmg-description-caption").wrapInner("");
                    }
                break;
                case "768":
                case "640":
                	$("#sw-mystart-mypasskey").prependTo($("#gb-channel-list-768"));  
                    $(".hp #gb-social-media-icons").prependTo($("#hp-slideshow-outer"));
                    $(".sp #gb-social-media-icons").insertAfter($(".sp-breadcrumbs"));

                    if(!$("#hp-slideshow .mmg-description-title span").legnth || !$("#hp-slideshow .mmg-description-caption span").length) {
                        $("#hp-slideshow .mmg-description-title").wrapInner("");
                        $("#hp-slideshow .mmg-description-caption").wrapInner("");
                    }

                    //DOC set height on mmg desc to keep content from jumping
                    var scrolled = 0;
                    $(window).scroll(function(e) {
                        scrolled = $(window).scrollTop();   
                       	$.each(mmgArrays, function(index, mmg) {
                            if(scrolled >= mmg.loc && mmg.scrollLock === 0) {
                                $(".mmg-description-outer", mmg.appID).height($(".mmg-description-outer", mmg.appID).height());
                                mmg.scrollLock = 1;
                            } else if(scrolled < mmg.loc && mmg.scrollLock == 1){
                                $(".mmg-description-outer", mmg.appID).height("auto");
                                mmg.scrollLock = 0;
                            }
                        });                     
                    });                 
                break;
                case "480":
                case "320":
                	$("#sw-mystart-mypasskey").prependTo($("#gb-channel-list-768"));  
                    $("#gb-social-media-icons").appendTo($("#gb-header-left"));

                    if(!$("#hp-slideshow .mmg-description-title span").legnth || !$("#hp-slideshow .mmg-description-caption span").length) {
                        $("#hp-slideshow .mmg-description-title").wrapInner("");
                        $("#hp-slideshow .mmg-description-caption").wrapInner("");
                    }

                    //DOC set height on mmg desc to keep content from jumping
                    var scrolled = 0;
                    $(window).scroll(function(e) {
                        scrolled = $(window).scrollTop();   
                       	$.each(mmgArrays, function(index, mmg) {
                            if(scrolled >= mmg.loc && mmg.scrollLock === 0) {
                                $(".mmg-description-outer", mmg.appID).height($(".mmg-description-outer", mmg.appID).height());
                                mmg.scrollLock = 1;
                            } else if(scrolled < mmg.loc && mmg.scrollLock == 1){
                                $(".mmg-description-outer", mmg.appID).height("auto");
                                mmg.scrollLock = 0;
                            }
                        });                     
                    }); 
                break;
            }
        },

        "DynamicStyles": function() {
        	//Variables
            var regionDBackgroundSrc = $.trim('/cms/lib/MS01910533/Centricity/Template/GlobalAssets/images///Background Images/jacksonbg.jpg');
            var srcCheck = regionDBackgroundSrc.indexOf("Faces/default-man.jpg") > -1;

            var styles = "";

            if((regionDBackgroundSrc != "") && (!srcCheck)) {
                styles += '.hp-row.two:before { \n'+   
                              "\t background: url('"+regionDBackgroundSrc+"') no-repeat fixed; \n"+
                              "\t background-size: cover; \n"+
                          '}\n';
            } else {
                styles += '.hp-row.two:before { \n'+   
                              "\t background: url('/cms/lib/MS01910533/Centricity/Template/30/hp-region-d-background.jpg') top center no-repeat;\n"+
                              "\t background-size: cover; \n"+
                          '}\n';
            }

            $('head').append(
                $('', {
                    id: 'cs-dynamic-background-styles',
                    html: styles
                })
            );

        }, 

