            $.schoolListMinibase({
                'miniBaseDomainID': 4,
                'miniBaseModuleInstanceID': 4882,
                'cache': true,
                'schoolListReady': function(schoolList) {
                    /*
                    The returned value is an array of objects and each object is a school record.
                    The fields are: schoolList.schoolName, schoolList.schoolType, schoolList.schoolUrl, schoolList.schoolAlias, schoolList.schoolVisible
                    */
                    var middleSchools = '';
                    var highSchools = '';
                    var elemSchools = '';
                    var altSchools = '';
                    var elemSchoolHit = 0;

                    for(var i in schoolList) {
                        if(schoolList[i].schoolVisible.toLowerCase() == 'true') {
                            switch( schoolList[i].schoolType ){
                                case "High":
                                    highSchools += '
' + schoolList[i].schoolName + '';
                                    break;
                                case "Middle":
                                    middleSchools += '
' + schoolList[i].schoolName + '';
                                    break;
                                case "Elementary":
                                    if(elemSchoolHit%12 == 0) elemSchools += ''; //break them into lists of 12...
                                    elemSchools += '
' + schoolList[i].schoolName + '';
                                    elemSchoolHit++;
                                    break;
                                case "Alternative":
                                    altSchools += '
' + schoolList[i].schoolName + '';
                                    break;
                            }
                        }
                    }

                    var megaSchoolDom = '
' +
                                            '
' +
                                                '
' +
                                                  '
' +
                                                      '
Elementary Schools' +
                                                      '
' + elemSchools + '' +
                                                  '' +
                                                '' +
                                                '
' +
                                                  '
' +
                                                      '
Middle Schools' +
                                                      '
' + middleSchools + '' +
                                                  '' +
                                                '' +
                                                '
' +
                                                  '
' +
                                                      '
High Schools' +
                                                      '
' + highSchools + '' +
                                                  '' +
                                                  '
' +
                                                      '
Special Schools' +
                                                      '
' + altSchools + '' +
                                                  '' +
                                                '' +
                                            '' +
                                        '';

                    $('#cs-schools-list .cs-dropdown-list').append(megaSchoolDom);
                }
            });

