        "MyStartDropdownActions": function(params) {
        	// FOR SCOPE
            var template = this;

            var dropdownParent = params.dropdownParent;
            var dropdownSelector = params.dropdownSelector;
            var dropdown = params.dropdown;
            var dropdownList = params.dropdownList;

            $(dropdownParent + " " + dropdownList + " a").attr("tabindex", "-1");

            // MYSTART DROPDOWN SELECTOR HOVER EVENT
            $(dropdownSelector).hover(function(e) {
                e.preventDefault();

                $(this).attr("aria-expanded", "true").parent().addClass("open").find(dropdown).attr("aria-hidden","false").slideDown(300, "swing");
        	}, function(e) {

            });

            // MYSTART DROPDOWN SELECTOR KEYDOWN EVENTS
            $(document).on("keydown", dropdownSelector, function(e) {
                // CAPTURE KEY CODE
                switch(e.keyCode) {
                    // CONSUME LEFT AND UP ARROWS
                    case template.KeyCodes.enter:
                    case template.KeyCodes.space:
                        e.preventDefault();

                        // IF THE DROPDOWN IS OPEN, CLOSE IT
                        if($(dropdownParent).hasClass("open")){
                        	$("+ " + dropdown + " " + dropdownList + " a").attr("tabindex", "-1");
                            $(this).attr("aria-expanded", "false").parent().removeClass("open").find(dropdown).attr("aria-hidden", "true").slideUp(300, "swing"); 
                        } else {
                        	$(this).attr("aria-expanded", "true").parent().addClass("open").find(dropdown).attr("aria-hidden", "false").slideDown(300, "swing", function(){
                            	if($(dropdownList + " .goog-te-combo", this).length) {
                                	$(dropdownList + " .goog-te-combo", this).focus();
                                } else if($(dropdownList + " #gb-search-input", this).length) {
                                	$(dropdownList + " #gb-search-input", this).focus();
                                } else {
                                	$(dropdownList + " li a:eq(0)", this).attr("tabindex", "0").focus();
                                }
                            });
                        }
                    break;

                    // CONSUME TAB KEY
                    case template.KeyCodes.tab:
                    	e.preventDefault();

                        $("+ " + dropdown + " " + dropdownList + " a").attr("tabindex", "-1");
                        $(this).attr("aria-expanded", "false").parent().removeClass("open").find(dropdown).attr("aria-hidden", "true").slideUp(300, "swing");

                        if(e.shiftKey) {
                            $(this).parent().prev().find(":focusable").focus();
                        } else {
                        	var index = $(":focusable").index(this);

                        	$(":focusable").eq(index+1).focus();
                        }
                    break;

                    // CONSUME LEFT AND UP ARROWS
                    case template.KeyCodes.down:
                    case template.KeyCodes.right:
                        e.preventDefault();

                        $("+ " + dropdown + " " + dropdownList + " a").attr("tabindex", "-1");
                        $("+ " + dropdown + " " + dropdownList + " li:first-child > a", this).attr("tabindex", "0").focus();
                    break;
                }
            });

            // MYSTART DROPDOWN LINK KEYDOWN EVENTS
            $(document).on("keydown", dropdownList + " li a", function(e) {
                // CAPTURE KEY CODE
                switch(e.keyCode) {
                    // CONSUME LEFT AND UP ARROWS
                    case template.KeyCodes.left:
                    case template.KeyCodes.up:
                        e.preventDefault();
                        var index = $(this).closest(dropdownList).find("a").index(this);

                        // IS FIRST ITEM
                        if(index == 0) {
                            // FOCUS DROPDOWN BUTTON
                            $(this).closest(dropdownList).find("a").attr("tabindex", "-1");

                        	$(this).closest(dropdownList).find("a").last().attr("tabindex", "0").focus();
                        } else {
                            // FOCUS PREVIOUS ITEM
                            $(this).closest(dropdownList).find("a").attr("tabindex", "-1");

                        	$(this).closest(dropdownList).find("a").eq(index-1).attr("tabindex", "0").focus();
                        }
                    break;

                    // CONSUME RIGHT AND DOWN ARROWS
                    case template.KeyCodes.right:
                    case template.KeyCodes.down:
                        e.preventDefault();
						var index = $(this).closest(dropdownList).find("a").index(this);
                        var length = $(this).closest(dropdownList).find("a").length;

                        // IS LAST ITEM
                        if(index == length-1) {
                            // FOCUS FIRST ITEM
                            $(this).closest(dropdownList).find("a").attr("tabindex", "-1");

                        	$(this).closest(dropdownList).find("a").eq(0).attr("tabindex", "0").focus();
                        } else {
                            // FOCUS NEXT ITEM
                            $(this).closest(dropdownList).find("a").attr("tabindex", "-1");

                        	$(this).closest(dropdownList).find("a").eq(index+1).attr("tabindex", "0").focus();
                        }
                    break;

                    // CONSUME TAB KEY
                    case template.KeyCodes.tab:
                    	if(e.shiftKey) {
                        	e.preventDefault();

                        	// FOCUS DROPDOWN BUTTON
                            $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                            $(this).closest(dropdownParent).find(dropdownSelector).focus();
                        }
                    break;

                    // CONSUME HOME KEY
                    case template.KeyCodes.home:
                        e.preventDefault();

                        // FOCUS FIRST ITEM
                        $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                        $(this).closest(dropdownList).find("li:first-child > a").attr("tabindex", "0").focus();
                    break;

                    // CONSUME END KEY
                    case template.KeyCodes.end:
                        e.preventDefault();

                        // FOCUS LAST ITEM
                        $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                        $(this).closest(dropdownList).find("li:last-child > a").attr("tabindex", "0").focus();
                    break;

                    // CONSUME ESC KEY
     				case template.KeyCodes.esc:
                    	e.preventDefault();

                    	// CLOSE MENU
                    	$(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                        $(this).parents(dropdownParent).find(dropdownSelector).focus().attr("aria-expanded", "false").parent().removeClass("open").find(dropdown).attr("aria-hidden", "true").slideUp(300, "swing"); 
					break;
                }
            });

            $(dropdownParent).mouseleave(function() {
				$(dropdownList + " a", this).attr("tabindex", "-1");
				$(dropdownSelector, this).attr("aria-expanded", "false").parent().removeClass("open").find(dropdown).attr("aria-hidden", "true").slideUp(300, "swing"); 
            }).focusout(function() {
                var thisDropdown = this;

                setTimeout(function () {
                    if(!$(thisDropdown).find(":focus").length) {
                        $(dropdownSelector, thisDropdown).attr("aria-expanded", "false").parent().removeClass("open").find(dropdown).attr("aria-hidden", "true").slideUp(300, "swing");
                    }
                }, 500);
            });
        },

        "Header": function() {
        	//For Scope
            var _this = this;

        	//Emergency Announcement
            if($("#gb-emergency-announcement div.ui-widget.app.announcements .ui-articles li").length) {
            	$("#gb-emergency-announcement-outer").attr("aria-hidden", false).show();
            }

            $(document).on("click keypress", "#gb-emergency-announcement-open-button", function(e) { 
                if(_this.AllyClick(e)) {
                    $("#gb-emergency-announcement").attr("aria-hidden", false).slideDown(250);
                    $("#gb-emergency-announcement-open-button").fadeOut(250);
                    $("#gb-emergency-announcement-close-button").focus();


                }
            });

            $(document).on("click keydown", "#gb-emergency-announcement-close-button", function(e) { 
                e.preventDefault();

                if(_this.AllyClick(e)) {
                    $("#gb-emergency-announcement").attr("aria-hidden", true).slideUp(250);
                    $("#gb-emergency-announcement-open-button").fadeIn(250, function(){
                    	$("#gb-emergency-announcement-open-button").focus()
                    });
                }
            });

            // ADD LOGO
            this.LoadImage(".gb-logo", '/cms/lib/MS01910533/Centricity/Template/GlobalAssets/images///Faces/default-man.jpg', '/cms/lib/MS01910533/Centricity/Template/30/gb-logo.png', true);

            // ADD SITENAME
            var siteNameOne = $.trim("");
            var siteNameTwo = $.trim("");
            if((siteNameOne == "") && (siteNameTwo == "")) {

            } else if((siteNameOne != "") && (siteNameTwo == "")) {
                $(".gb-sitename").prepend("
" + siteNameOne + "");
            } else if((siteNameOne == "") && (siteNameTwo != "")) {
                $(".gb-sitename").prepend("
" + siteNameTwo + "");
            } else if((siteNameOne != "") && (siteNameTwo != "")) {
                $(".gb-sitename").prepend("
" + siteNameOne + "" + siteNameTwo + "");
            }
        },

        "ChannelBar": function() {
        	$(".hidden-sections").remove();

        	$("#navc-CA").insertBefore($(".sw-channel-item").not("#navc-HP").first());
        	$("#navc-CA a").attr("aria-label", "Calendar")

            $(".sw-channel-item").unbind('hover');
            $(".sw-channel-item").hover(function(){
                $(".sw-channel-item ul").stop(true, true);
                var subList = $(this).children('ul');
                if ($.trim(subList.html()) !== "") {
                    subList.slideDown(300, "swing");
                }
                $(this).addClass("hover");
            }, function(){
                $(".sw-channel-dropdown").slideUp(300, "swing");
                $(this).removeClass("hover");
            });

            $(".sw-channel-item").each(function(){
            	if($(".sw-channel-dropdown li", this).length == 0) {
                	$(this).addClass("no-dropdown");
                }
            });

            $(".sw-channel-item").on('mouseenter mouseleave', function (e) {
                if ($('.sw-channel-dropdown', this).length) {
                    var elm = $('.sw-channel-dropdown', this);
                    var off = elm.offset();
                    var l = off.left;
                    var w = elm.width();
                    var docH = $("header").height();
                    var docW = $("header").width();

                    var isEntirelyVisible = (l + w <= docW);

                    if (!isEntirelyVisible) {
                        $(this).addClass('edge');
                    } else {
                        $(this).removeClass('edge');
                    }
                }
            });
        },

