        "Slideshow": function() {
            if($("#sw-content-container11 .ui-widget.app.multimedia-gallery").length) {
                var mmg = eval("multimediaGallery" + $("#sw-content-container11 .ui-widget.app.multimedia-gallery:first").attr("data-pmi"));
                mmg.props.defaultGallery = false;

                $("#sw-content-container11 .ui-widget.app.multimedia-gallery:first").csMultimediaGallery({
                    "efficientLoad" : true,
                    "imageWidth" : 1500,
                    "imageHeight" : 910,
                    "mobileDescriptionContainer": [768, 640, 480, 320], // [960, 768, 640, 480, 320]
                    "galleryOverlay" : false,
                    "linkedElement" : [], // ["image", "title", "overlay"]
                    "playPauseControl" : true,
                    "backNextControls" : true,
                    "bullets" : false,
                    "thumbnails" : false,
                    "thumbnailViewerNum": [4, 4, 3, 3, 2], // NUMERICAL - [960 view, 768 view, 640 view, 480 view, 320 view]
                    "autoRotate" : true,
                    "hoverPause" : true,
                    "transitionType" : "fade", // fade, slide, custom
                    "transitionSpeed" : 4,
                    "transitionDelay" : 9,
                    "fullScreenRotator" : false,
                    "fullScreenBreakpoints" : [960], // NUMERICAL - [960, 768, 640, 480, 320]
                    "onTransitionStart" : function(props) {
                    	if(!$(".mmg-description-title span", props.element).legnth || !$(".mmg-description-caption span", props.element).length) {
                            $(".mmg-description-title", props.element).wrapInner("");
                            $(".mmg-description-caption", props.element).wrapInner("");
                    	}
                    },
                    "allLoaded" : function(props) {
                    	if(!$(".mmg-description-title span", props.element).legnth || !$(".mmg-description-caption span", props.element).length) {
                            $(".mmg-description-title", props.element).wrapInner("");
                            $(".mmg-description-caption", props.element).wrapInner("");
                    	}

                    	$(".mmg-control", props.element).wrapAll('
');
                        $(".mmg-control.play-pause", props.element).insertAfter($(".mmg-control.back", props.element));
                     } // props.element, props.mmgRecords
                });
            }
        },

